const i=[{number:1,title:"TRIVIENIO DIEVO PALAIMINIMAS","sub-title":"GYRIUS JAM",languages:{russian:4,english:7},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/7","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0007_full.mp3",verses:[["Tau šlovė, šlovė, o Tėve!","Tau šlovė, šlovė Sūnau!","Tau šlovė, šlovė o Dvasia!","O šlovė, Trejybe, Tau!"],["Girkit Tėvą — Jis sumanė!","O Sūnus atliko tai!","Perdavė Dvasia šventoji!","Trise dirba vieningai!"]],chorus:["Girkim Dievą, girkim Dievą!","Jam šlovė, trys Viename!","Šlovinkime mūsų Dievą! ","Nuostabius Jo darbus skelbkime!"]},{number:2,title:"TĖVO, KAIP DVASIOS, GARBINIMAS",languages:{russian:7,english:11},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/11","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0011_full.mp3",verses:[["O Tėve, Tu tiesos Dvasia.","Švenčiausias iš visų;","Mes Garbinam Tave dvasia","Ir šaukiamės tiesoj."],["Kad garbintume mes Tave,","Sukūrei dvasią mums.","Todėl atsako mūs dvasia:","Mes viena dvasioje."],["Per Sūnų, Tėve, atėjai.","Sūnus — dabar Dvasia.","Pats Dievas mūsų dvasioje —","Malonė amžina."],["Sūnus tai Tavo Žodis mums.","Tas Žodis tai Dvasia.","Tu kaip gyvybė atėjai,","Kad atgaivint mane."],["Tava Dvasia per viską ves,","Kad sektume mes Juo.","Taip dvasiniais mes tapsime,","Ramybę rasime."],["Mes melsimės tik dvasioje","Ir žvelgsim į Tave.","Išlaisvint Dvasią norime,","Išreikšti tik Tave."],["Tu Tėvas ir Dvasia esi,","Priimki gyrių mūs.","Mes dvasioje ir tiesoje","Tau lenkiamės mes čia."]]},{number:3,title:"TĖVO, KAIP MEILĖS ŠVIESOS, GARBINIMAS",languages:{russian:9,english:13},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/13","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0013_full.mp3",verses:[["Viešpatie, esi Tu meilė,","Viešpatie, esi šviesa.","Sūnuje esi gyvybė,","Dalini Save per ją."],["Tik iš meilės Tave regim,","Šviesoje — ką atlikai.","Vidų mūs pripildei meile,","Kad mums paskui šviesą eit."],["Per malonę lieji meilę,","Mums tiesa suteiks šviesos.","Priartėjam mes iš meilės,","Regime Tave šviesoj."],["Dievo prigimtį davei mums","Tu Golgotoj, meilėje.","Mūs mintis šviesa apšviečia,","Krauju apsivalome."],["Pamilai, tapai gyvybe,","Kad bendrautume per ją.","Šviesa Tavo veja tamsą - ","Mes tada vienybėje."],["Tik per kraują ir per šviesą","Patepimas mumyse.","Prigimtis Tavoji — meilė","Gausiai liejas į mane."],["Meilėje vaikai mes Tavo,","„Abba Tėve“ — šaukiame.","Ir šviesa išvis mūs tamsą,","Kaip Sūnus regėsime."],["Per malonę turim šviesą,","Tavo meilę turime.","Ir per tai Tave pažįstam,","Iš širdies dėkojame."]],chorus:["Viešpatie, meilė Tu,","Viešpatie, Tu šviesa.","Sūnuje esi gyvybė,","Dalini Save per ją."],"chorus-2":["O, malonė. O, tiesa","Štai ir meilė, ir šviesa.","Tave girsim mes be galo,","Ir visai pažinsime."]},{number:4,title:"TĖVO GARBINIMAS UŽ JO NEMIRTINGUMĄ",languages:{russian:10,english:14},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/14","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0014_full.mp3",verses:[["Esi nemirtingas, nematomas Tu.","Šlovė Tavo dengia Tave nuo žmonių.","Didingas, Tu Dieve, Tavy dienos mūs.","Mes garbinsim Tave, o Tėve brangus."],["Valdai Tu visatą Savąja galia,","Ir nieks apriboti negali Tavęs.","Teisus Tavo teismas kaip Tavo kalnai,","Kaip lietūs dangaus Tavo meilės srautai."],["Kas buvo mirty, tam gyvybę teiki.","Esi visame, Tu mums viskas esi.","Mes žydim ir vystam kaip lauko gėlė,","O Dieve, Tavy nė dalelės mirties."],["O Dieve, koksai Tu Didingas esi,","Kad net angelai veidus dengia visi.","Tu Tėve mūs Didis, šlovės ir šviesos.","Visatos Kūrėjas — Tavy nėr tamsos."],["Esi nemirtingas, nematomas Tu.","Šlovė Tavo dengia Tave nuo žmonių.","Didingas Tu, Dieve, Tavy dienos mūs.","Mes garbinsim Tave, o Tėve brangus."]]},{number:5,title:"TĖVO GARBINIMAS UŽ JO ŠVENTUMĄ",languages:{russian:18,english:22},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/22","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0022_full.mp3",verses:[["Šventas Tėve Dangiškasis,","Tavo vardą aukštinam,","Mes Šventu Tave vadinam,","Džiaugiamės ir garbinam."],["Tu teisus per amžius, Tėve.","Myli Tavo širdis mus.","Tavo prigimtį — šventumą","Kristus perduoda į mus."],["Jėzaus Viešpaties krauju Tu","Amžiams mus pašventinai.","Ta tiesa, kur Tavo Žody,","Mus vergus išlaisvinai."],["Taip šventaisiais padarei mus","Tu sava Šventa Dvasia.","Tiktai Ja tegu šventėja","Kūnas, siela ir dvasia."],["Jėzaus šventąją gyvybę","Per malonę gavome.","Ir visi šventumą Tavo","Mes dalinti galime."],["Ir kada Šventam mieste mes","Būsim viena šventume.","Iki žemės pakraščių net","„Šventas Tu“ giedosime."]]},{number:6,title:"TĖVO GARBINIMAS UŽ JO NAUJUMĄ",languages:{russian:12,english:16},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/16","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0016_full.mp3",verses:[["Neblėstantis, Tu Tėvas mūs","Naujumas Tavyje.","Tu kaip rasa gaivinantis.","Pagirdai Tu mane."],["Tave pažįstame visi","Ir vėl, ir vėl naujai.","Tu atgaivą visiems teiki.","Nors metai bėga greit."],["Palaiminimus suteiki","Ir vėl, ir vėl naujus","Naujoji Sandora Tava","Atvėrė kelią mums."],["Tu naują širdį mums davei","Ir dvasią atgimdei.","Dabar mes esame visi","Naujajam Kūriny."],["Atnaujini Tu, Tėve, mus —","Kas sena naikini.","Kiekvieną dieną naują Tu","Gyvybę suteiki."],["Dangus ir žemė bus nauji.","Ir viskas nauja bus","Gyvybės medis duos vaisius","Per ištisus metus."],["Naujumas Tavo amžinas.","Tu Naujas visame.","Nauja giesme mes girsime","Per amžius tik Tave."]],chorus:["Tu nesikeičiantis Tėvas,","Tu jaunas amžinai.","Per amžius atskleidi Save","Naujovėj nuostabioj."]},{number:7,title:"TĖVO GARBINIMAS JO DIDYBĖ",languages:{russian:13,english:17},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/17","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0017_full.mp3",verses:[["O Tėve, kai žiūriu į kūriniją.","Į dangų, žemę ir jų stebuklus.","Suvokti mums gebėjimo pristinga —","Tu taip apreiški Savo jėgą mums."],["Priimdamas malonę Tavo, Tėve.","Galvoju apie Tavo Sūnų aš.","Jis mirė, kad Naujasis Kūrinys Jo","Gyvybę teiktų Jo pilnatvėje."],["Susirinkime, Jo malonėj būnant,","Matau, kad gyvas daugelyje Tu.","Ir šitaip ugdoma Bažnyčia Tavo.","Ir iš vidaus per mus reiškiesi Tu."],["Nauja Jeruzale visi mes būsim.","Pilnatvės amžiaus laukiam su džiaugsmu.","Ir su naujaisiais dangumi ir žeme —","Mes tapsime Tavu išreiškimu."]],chorus:["Mana dvasia štai gieda, Tėve, Tau.","Koks Didis Tu, koks nuostabus!","Ir amžinai giedosim giesmę šią:","Koks Didis Tu, koks nuostabus."]},{number:8,title:"KAS APSAKYS DIDYBĘ JO",verses:[["Kas apsakys didybę Jo ir valdžią pamatuos,","Jis trykšta gyvybės jėga!","Priklaups karaliai žemės šios ir Jėzaus","vardui nusilenks, šaltiniui Meilės ir Šviesos! }2k","Kas apsakys didybę Jo.","Kas apsakys Meilę Kristaus Viešpaties?","Kas apsakys didybę Jo?","Kas apsakys šviesą Jo?","Kas apsakys meilę Kristaus?"]]},{number:9,title:"TĖVO GARBINIMAS JO MEILĖ",languages:{russian:26,english:30},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/30","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0030_full.mp3",verses:[["O, kokią meilę mums davei!","Dėkojam Tau išties.","Tau, Tėve, lenkiamės žemai,","Dėkojam iš širdies."],["Atvėrei Savo tikslą mums.","Sumanymą širdies.","Save apreiškei Sūnuje.","Tu mus vedi išties."],["Tu brangų Sūnų mums davei —","Jis numirė už mus,","Kad taptume mes sūnumis,","Paveldėję turtus."],["Gyvybę turime Jame.","Tu Tėvu mūs tapai.","Esybę Savo ir Save","Tu mums atidavei."],["Tava Dvasia mūs dvasioje.","Tau „Abba“ šaukiame.","Mes gimę esame Jame.","Į Jį mes keičiamės."],["Šlovėn atvesti sūnus Tu —","To troškai visada.","Mes tapsim atvaizdu Sūnaus","Duota Tava jėga."],["Tu atmainysi mus visus —","Kiekvienas tau brangus.","Atvesi Tu į šlovę mus","Mes tikim iš tiesų."],["O, kokią meilę suteikei!","Mes Tau dėkojame!","Per amžius šlovinsim Tave","Savose širdyse."]]},{number:10,title:"TĖVO GARBINIMAS UŽ JO ATPIRKIMĄ",languages:{russian:31,english:39},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/39","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0039_full.mp3",verses:[["Kas gali daugiau atiduot negu Jis:","Dėl mūsų atidavė Sūnų numirt.","Krauju Jis Savuoju nuplovė visus,","Gyvenimo tikro atkėlė vartus."],["Atpirko visus mus brangiuoju krauju.","„Tikėk“ — Viešpats sako Savuoju Žodžiu.","Nors tu nuodėmėj, nebėra tau vilties,","Jo šaukis, tada nebeliks jau kaltės."],["Kas gali daugiau atiduot negu Jis:","Per Sūnų palaimą mes turim visi.","Bet džiaugsmas tada neturės jau ribų,","Kada susitiksime su Viešpačiu."]],chorus:["Tau šlovė! Tau šlovė! Te išgirsta Tave!","Tau šlovė! Tau šlovė! Džiaugias žemė visa!","Ateikit per Sūnų, šlovė Tėvui Jo!","Nuostabūs darbai, ką atliko Jisai!"]},{number:11,title:"TĖVO GARBINIMAS JO ATPIRKIMAS",languages:{russian:32,english:40},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/40","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0040_full.mp3",verses:[["Tau dėkojam visi,","Tėve, Sūnų davei.","Tik per Jį atpirkti,","Nes Tu mus pamilai."],["Kristus Gelbėtojas —","Tavo meilės Sūnus.","Jau išaukštintas Jis,","Nes Jis mirė už mus."],["Tu šlovingas esi,","Davei Dvasią šviesos.","Ji apreiškė mums Kristų —","Neliko tamsos."],["Tėvą šloviname,","Kurs pamilo taip mus.","Jėzaus išpirkimu","Meilę rodo Jis mums."],["Vėl mes giriam Tave,","Nes mes meilės pilni.","Ir ugnis ta iš aukšto","Uždegs mums širdis."]],chorus:["Aleliuja! Šlovė Dievui!","Aleliuja! Amen!","Aleliuja! Šlovė Dievui!","Tave aukštinam!"]},{number:12,title:"TĖVO GARBINIMAS JO IŠPIRKIMAS",languages:{russian:33,english:41},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/41","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0041_full.mp3",verses:[["Niekada mes nepavargsime kartoti tai:","Tėvui šlovė, Aleliuja!","Ir kiekvienas dvasioj girsime Tave linksmai:","Tėvui šlovė, Aleliuja!"],["Meilės jūroj mes paskendę esame visi:","Tėvui šlovė, Aleliuja!","Ir malonę Tavo geriame kuri apsti:","Tėvui šlovė, Aleliuja!"],["Iriamės į šlovę, nes taip sakė Viešpats mums:","Tėvui šlovė, Aleliuja!","Kad visa esybe savo žvelgtume į Jį:","Tėvui šlovė, Aleliuja!"],["Naują giesmę Tau giedosime su padėka:","Tėvui šlovė, Aleliuja!","Nenustosim girti Gelbėtojo niekada:","Tėvui šlovė, Aleliuja!"]],chorus:["Viešpaties vaikų šimtai giesmę gieda Tau linksmai,","Iš šlovės į šlovę Jis taip parves mus sūnumis.","Ir visi mes su džiaugsmu, skubam pas Karalių:","Tėvui šlovė, Aleliuja!"]},{number:13,title:"TĖVO GARBINIMAS JO IŠAUKŠTINIMAS",verses:[["Štai į Tave žvelgiu,","O Dieve Visagali!","Aš šlovinu Tave, lenkiuosi,","Giedu gyrių Tau.","O mano Amžinasis Tėve,","Garbinsiu Tave per amžius,","Tavo vardą aukštinsiu,"]]},{number:14,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO VARDAS",languages:{russian:52,english:67},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/67","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0067_i.mp3",verses:[["Šį vardą „Jėzus“ aš myliu","Kaip muzika jis man skambus","Jame aš džiaugsmą atrandu.","Saldus Jo vardas — Jėzus!"],["Prisimena mane Jisai,","Dalijamės kartu vargais,","Jis nerimą pašalina.","Aš myliu Jėzaus vardą."],["Šis vardas skamba taip švelniai","Man širdį džiugina Jisai.","Jį tariant dingsta ašaros:","Šlovinkim Jėzaus vardą."],["Nėr žodžių viską išsakyt,","Koksai saldus man vardas šis.","Mes nepaliaujant šlovinsim","Viešpaties Jėzaus vardą."]],chorus:["Jėzus — vardas man saldus","Jėzus — toks pats visados.","Jėzau, te šventieji Tau","Giedos per amžių amžius."]},{number:15,title:"TĖVO GARBINIMAS","sub-title":"JO IŠPIRKIMAS",languages:{russian:60,english:79},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/79","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0079_i.mp3",verses:[["Didingas ir šlovingas Tu,","Karalius Jėzus mūs.","Jis už žmoniją numirė,","Atpirko mus visus."],["Aukščiau nėra jokių vardų,","Kaip Jėzaus Viešpaties.","Jis sėdi Dievo dešinėje,","Jo vardo šaukiamės."],["Mums davė Gelbėtoją čia,","Pavergtiems nuodėmės.","Numatęs buvo gelbėti","Visus mus nuo mirties."],["Jis užvaldys visas žemes","Ir karalystes.","Jis bus vaidinamas visų","Galinguoju Dievu."]],chorus:["Šlovink vardą šį, šlovink vardą šį.","Vardą Viešpaties šlovinki.","Šlovink vardą šį, šlovink vardą šį.","Vardą Viešpaties šlovinki."]},{number:16,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO VARDAS",languages:{russian:64,english:1086},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1086","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1086_i.mp3",verses:[["Koks tas vardas — Jėzus!","Toks didis yra jis mums.","Danguje ir žemėje nėra aukščiau jo.","Vardą šį įkvėpsim mes —","Tamsa pranyks, mirtis išnyks,","Nes sugrįžta Viešpats Jėzus vėl pas mus."],["Džiaugiamės visa širdim","Šlovė mūs Tėvui — Amen!","Išpažįstam Tavo vardą, Viešpatie.","Atiduoti gyrių Tau —","Tai pasimėgavimas mums.","O, mes mylim, Jėzau, šaukiamės Tavęs!"]]},{number:17,title:"AŠ GYVENAU TUŠČIAI",verses:[["Aš gyvenau tuščiai, ir tikslo nemačiau,","Kol išgirdau Tą žinią, kad yra kažkas daugiau.","Pradžioje netikėjau — taip paprastai išėjo..."],["Vėliau, pabandęs tyliai, pakviesti Jį vardu","Pasijutau lyg sunkus ligonis tapęs vėl sveiku.","Versmė gyvybės teka ir skatina mane..."],["Vos tik pakvietęs Jėzų, ramybę pajutau.","Jisai mane pripildė, Jėzaus vardą sušukau!","Anksčiau net nesvajojau pajaust tikrovę Jo..."],["Dabar esu laimingas, patenkintas su Juo.","Mana dvasia džiaugsminga ir maitinasi tik Juo.","Gyvybės medis auga, todėl dabar tereikia..."]],chorus:["Ištarti Jo Vardą: „O, Jėzau“","Sakyti: „Amen“ Jo šlovėj,","Pripildyti dvasią gyvybe —","Dievo gyvybė manyje,","Užliejanti visa manyje,","Dievo gyvybė manyje!"]},{number:18,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"UŽ JO ĮSIKŪNIJIMĄ",languages:{russian:66,english:82},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/82","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0082_i.mp3",verses:[["Palikęs šlovę ir dangaus gerovę,","Jėzus atėjo čia išgelbėti mane.","Ėdžiose gimęs ir daug vargo matęs;","Liūdesio, ašarų, kančių Žmogus."],["Malonės Dievas atpirkimo dieną,","Tamsios mirties skausme atidavė Save.","Jis rūpestingas, jautrus, galingas,","Jis prisikėlė — aš esu Jame."],["Žemiškam kūne Dievas Savo Sūnų","Sutiko paaukot, mums laisvę dovanot.","Golgotoj miręs ir prisikėlęs!","Dabar žinau Tu Didis „Aš Esu“."]],chorus:["Kaip aš Jį myliu, kaip aš Jį myliu!","Mano alsavimas mano šviesa.","Didis Kūrėjas mane laimėjo,","Dievo pilnatvė gyva jame."]},{number:19,title:"GYRIUS VIEŠPAČIUI","sub-title":"UŽ JO ĮSIKŪNIJIMĄ",languages:{russian:67,english:84},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/84","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0084_i.mp3",verses:[["Angelai paskelbė mums:","Štai Karalius gimė jums.","Jis sutaikins su Dievu","Žmones nuodėmingus.","Visos tautos, štai pakilkit,","Ir su angelais Jį girkit.","Mes Betliejui giedame,","Nes mūs Kristus gimė čia.","Mes Betliejui giedame,","Nes mūs Kristus gimė čia."],["Kristų lemta šlovint mums:","Dievas Amžinas Jis mus.","Iš mergelės, iš tyros","Gimęs amžių pabaigoj.","Dievas Pats kūne atėjo","Ir dėl mūsų daug kentėjo.","Dievo noras buvo toks:","Su žmogum būt nuolatos.","Dievo noras buvo toks:","Su žmogum būt nuolatos."],["Kad įvykdyt sandorą,","Atėjai į žemę šią.","Iš dangaus nužengęs Tu,","Prisikėlei dėl visų.","Tu gimei palikęs šlovę,","Mirties valdžią Tu sugriovei,","Kad pakelt mus Tu gimei,","Atgimimą mums davei.","Kad pakelt mus Tu gimei,","Atgimimą mums davei."],["Šauksmą mūsų Tu išgirsk","Ir mumyse pasilik.","Dvasioje kaip sėkla auk.","Valdžią nuodėmės sugriauk.","Seną prigimtį ištrink,","Savo antspaudą palik.","Jėzau, Tu tapai Dvasia,","Dalini Save per Ją.","Jėzau, Tu tapai Dvasia,","Dalini Save per Ją."]]},{number:20,title:"GYRIUS VIEŠPAČIUI","sub-title":"UŽ JO DIEVIŠKUMĄ",languages:{russian:46,english:57},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/57","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0057_full.mp3",verses:[["Tu Dievo atvaizdas esi,","O Viešpatie brangus.","Pilnatvę Dievo atnešei,","Davei pažint Jį mums."],["Tu Dievo Žodis, Viešpatie.","Gyvybė mūsų Tu.","Tikrovė Tu esi čia mums,","Malonė mūs visų."],["Tu — Dievo amžino Sūnus,","Tu — Dievo spindesys.","Esybe Dievo Tu esi,","Pats Dievas Tu esi."],["Pats Dievas įsikūnijo,","Tu viena su Dievu.","Ir nieks negali taip išreikšt,","Kaip Jį išreiškei Tu."],["Mes mėgaujamės Tavyje,","Gyvybe mūs esi.","Mes giriam Tėvą ir Tave,","Mes gyriaus kupini."]]},{number:21,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO IŠAUKŠTINIMAS",languages:{russian:97,english:132},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/132","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0132_full.mp3",verses:[["Atsisėdo Jėzus sostan,","Mūsų Viešpats danguje.","Taip išaukštintas Jis Dievo,","Vainikuotas Jo Šlove."],["Kaip žmogus pas mus atėjo","Dievo siųstas mirti čia.","Šis žmogus išaukštintasis","Prisikėlė šlovėje."],["Dievas Pats Jame gyveno,","Apsireiškė žmoguje.","Taip išaukštino Jis žmogų","Ir dabar jie taikoje."],["Susiliejo su žmogum Jis,","Apsireiškė žmoguje.","Susiliejęs su žmogum Jis","Taip dalinasi šlove."],["Ir danguj pašlovintasis —","Jis sugrįžo kaip Dvasia,","Ką Jis turi, ką įvykdė","Visa tai paskelbė čia."],["Mes lygiuojamės į Kristų,","Ir Bažnyčia spindi Juo.","Jo Dvasia mus šiandien ugdo,","Veda mus šlovės keliu."],["Štai dabar Valdovas mūsų","Atsisėdo sostan Jis.","Kristus — Gelbėtojas mūsų","Šlove vainikuotas Jis."]]},{number:22,title:"GYRIUS VIŠPAČIUI","sub-title":"UŽ JO ŽMOGIŠKUMĄ",languages:{russian:49,english:62},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/62","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0062_full.mp3",verses:[["Šlovinam Tave mes, Jėzau,","„Moters Sėkla“ Tu tapai.","Ir gimei Tu iš mergelės,","Tu dėl mūs Žmogum tapai.","Žmogiškąja prigimtim Tu","Tą Gyvatę pagavai.","Kryžium galvą jai sutraiškei,","Dievo planą įvykdei."],["Įsikūnijai į žmogų,","Kūnu ir krauju tapai.","Tu už mus visus kentėjai,","Priešą Tu sutriuškinai.","Jėzumi Tave vadino,","Šaukė Emanueliu.","Gelbėtojas mūs brangiausias,","Laisvę dovanojai Tu."],["„Paskutinis Tu Adomas“","„Žmogumi antru“ tapai.","Naująją Tu Kūriniją","Amžina valdžia valdai.","Ir Žmogaus Sūnum tapai","Tu žemėje gyvendamas,","Pasilieki Žmogumi Tu,","Nors esi ir danguje."],["Dievo paskirtu laiku Tu","Grįši, Viešpatie, pas mus.","Šlovėje Tave išvysim:","Dievo išraiška — Žmogus.","Kaip Žmogaus Sūnus ateisi,","Teismo sostan sėsi Tu.","Žmogumi per amžius liksi,","Nors ir būsi Tu Dievu."]],chorus:["Tavo puikume mes","Matom Dievo šlovę,","Ir visa pilnatvė Dievo","Buvo Žmoguje."]},{number:23,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO MEILĖ",languages:{russian:114,english:152},"hymnal.net-url":"https://hymnal.net/en/hymn/h/152","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0152_full.mp3",verses:[["Nesuvokiama Tavoji meilė mums, o Viešpatie.","Gilumu jai neprilygsta net tamsiausioji gelmė.","Tik iš meilės Tu numiręs,","Atiduodi Save man;","Įsitvirtinti Tavyje,","Tavo dalelyte tapt."],["Kas stebuklus apsakys šiuos ką Tu meilėje kuri?","Nuostabiausias šis stebuklas, kad Save man dalini.","Tavo meilė duoda viską","Kuo aprūpinti gali.","Aš galiu net semt gyvybę","Ir turtus tavus patirt."],["Viešpatie, esi Tu meilė — nieks negali taip mylėt.","Tik iš Tavo meilės galim sunkumus mes nugalėt.","Ir gausi Tava malonė","Liejas per dienų dienas;","Suteiki mums tikrą meilę —","Koks saldus Tu visada."],["Begalinė Tavo meilė — kas gi gali mus atsiet?","Tiktai tavo meilė gali amžiams mus kartu suliet.","Amžinybėj viena būsim;","Aš Tavy — Tu manyje","Ir patvirtinti galiu aš —","Esam viena meilėje."]]},{number:24,title:"GYRIUS VIEŠPAČIUI","sub-title":"UŽ JO MEILĘ",languages:{russian:116,english:154},"hymnal.net-url":"https://www.himnynas.com/hymns/24.html","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0154_i.mp3",verses:[["Negalime protu suvokti tai,","O Viešpatie, kaip Tu mus pamilai.","Bet norime pilnatvę meilės šios","Suvokti ją, nors jos negalim niekaip išmatuot."],["Nėra kaip meiles Tavo išsakyt,","Nors siela bando vis dar pasakyt.","Ir nusidėjėliams paskelbsiu aš:","Jo meilė gelbsti, nebespaus jau nuodėmių našta."],["Jo meilės neįmanoma išgirt,","Bet širdimi aš noriu tai patirt.","O Viešpatie, šis gyrius iš dangaus","Išsklaidė tamsą ir nuostabią šviesą pamačiau."],["Nors šiandien pažinimo trūksta man,","Nesugebu Tau žodžių aš atrast.","Pripildyk mano indą Savimi,","Gyva versme Tu mano širdį nuolat atgaivink."],["Jei tuščias indas, nebėra tėkmės;","Nei meiles žodžių, nieko nėr išties.","Bet drįstu aš ateiti pas Tave,","Šią teisę duoda Tavo meilė jau man išlieta."],["O Viešpatie, uždeki širdį man!","Ir veski, nes sunku man eit pačiam.","Tikėjimu man troškulį malšink,","Nuo žemiškų šaltinių Tu mane, meldžiu, atskirk."],["Kada regėsiu Tavo veidą aš,","Giedosiu, Viešpatie, aš giesmę šią.","Išaukštinsiu aš Tavo meilę mums:","Jos gylį, plotį, aukštį — nesuvokiama tai mums."]]},{number:25,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":" UŽ JO GROŽĮ",languages:{russian:123,english:170},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/170","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0170_full.mp3",verses:[["Viešpatie, puikus Jauniki,","Dievo duotas, mums brangus.","Patrauklus ir nuostabus Tu,","Mielas mūs visų širdims."],["O Pamiltas, kas gi gali","Tavo grožį apsakyt?","Brangumu nieks neprilygsta —","Meilę saugosim širdy."],["Tu puikiausias iš puikiųjų,","Tu šlovingas ir saldus.","Toks romus ir toks didingas —","Nieks negali Tau prilygt."],["Mūsų rūbai pilni miros,","Lūpos šlovina Tave.","O išbandymuose, Tėve,","Apkabinam mes Tave."],["Ir su šiuo džiaugsmo aliejum","Dievas patepė Tave,","Ir iš dramblio kaulo rūmų","Tau pakils šlovė visa."],["Tu palaimintas per amžius,","Pasiekei Tu pergalę.","Šlovėje Tu karaliauji,","Valdžioje, didybėje."],["Tu esi tautų troškimas,","Tu geriausias iš visų.","Tau giedosim gyrių amžiais —","Vertas juk esi tik Tu."]]},{number:26,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"UŽ JO GROŽĮ",languages:{russian:125,english:172},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/172","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0172_i.mp3",verses:[["Aš noriu, Viešpatie, Tavęs","Įkvėpti vėl ir vėl.","Kvepi labiau nei mira Tu,","Labiau nei tūkstančiai žiedų","Dangaus aromatu","Kvepi, o Jėzau Tu."],["Aš negaliu savų akių","Atplėšti nuo Tavęs.","Tu mano širdį pavergei,","Ją Savimi įkalinai.","Koks nuostabus esi,","Koks nuostabus esi."],["Tu mano Viešpats, draugas Tu,","Kaip man Tau atsiduot?","Nenoriu būti aš laisvu,","Aš noriu būt Tavu vergu","Ir amžiais Tau tarnaut,","Ir amžiais Tau tarnaut."],["O, visko man neišdainuot,","Koksai esi saldus.","Širdis melodijų pilna,","Jos išsiliejo šia daina","Ir sielą džiugina,","Ir sielą džiugina."],["O, visko man neišsakyt,","Kiek galima dar daug...","Kai Tu mane apkabini,","Visad Tu švelniai man tari:","Tau viskas bus gerai,","Tau viskas bus gerai."]]},{number:27,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JIS VISA APIMANTIS",languages:{russian:138,english:190},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/190","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0190_full.mp3",verses:[["Mąstydami apie Tave,","Tau lenkiamės, o Viešpatie.","Esi turtingas, nuostabus,","Visų širdims esi brangus."],["Tu Dievas amžinos tiesos,","Šviesos ir meilės Dievas Tu.","Gyvybės Dievas mums esi,","Mūs siela mėgaujas Tavim."],["Esi taip pat Tu ir Žmogus,","Toks tyras, geras, nuostabus.","Atidavė Tau Dievas mus —","Meilėj įkalintus vergus."],["Tapai kukliu Dievo vergu,","Paklusęs Jam — tarnauti, mums.","Ant kryžiaus nuolankiai ėjai","Išpirkti nuo mirties visus."],["Be to Karalius Tu esi,","Kad karaliaut gyvybėje.","Tu Dievo pateptas valdyt","Ir mes valdysime drauge."],["Mąstydami apie Tave,","Mes turim dalį Tavyje.","Ir Tavo meile apgaubti","Mes būsime Tava žmona."]],chorus:["Patenkinti Tavim viskuo.","Širdy mes giriame Tave.","Pranoksti mūsų svajones,","Mes esam sotūs Tavyje."]},{number:28,title:"GYRIUS VIEŠPAČIUI","sub-title":"",languages:{russian:131,english:181},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/181","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0181_i.mp3",verses:[["Avinėliui Jo bažnyčioj:","Gyrius! Garbė!","Danguje Jam skamba himnai:","Gyrius! Garbė!","Griuvo žemiškieji sostai,","Šlovę Jam tik atiduosim,","Nepailstamai giedosim:","Gyrius! Garbė!"],["Kas tik galit girkit Jėzų:","Gyrius! Garbe!","Giedam mūsų Atpirkėjui:","Gyrius! Garbė!","Lyg griaustinis aidi gyrius,","Tiktai Jam šlovė tekyla,","Ir per amžius nenutyla:","Gyrius! Garbė!"],["Skamba kanklės Jėzaus šlovei:","Gyrius! Garbė!","Liejas Jo gausi malonė:","Gyrius! Garbė!","Sumokėjo mūsų skolą,","Nuodėmes krauju nuplovė,","Veda mus į Dievo šlovę:","Gyrius! Garbė!"],["Sielvarte, skausme giedokit:","Gyrius! Garbė!","Juo tikėti nenustokit:","Gyrius! Garbė!","Šia nauja giesme visi mes","Gelbėtoją šlovinsime","Avinėlį girsime mes:","Gyrius! Garbė!"]]},{number:29,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PRISIMINIMAS",languages:{russian:177,english:1112},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1112","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0861_i.mp3",verses:[["Štai šlovinga Viešpaties puota.","Viešpatie, Tu mūs Valdovas,","Gyveni Savuos namuos.","Štai Šlovinga Viešpaties puota."],["Mums saldi ši Viešpaties puota.","Mes, šventieji, čia bendraujam.","Aleliuja! Paragaujam,","Koks saldumas Viešpaties puotos."],["Puotą Viešpaties branginame.","Mes čia skelbiam Tavo mirtį.","Sielą norime palikti,","Duoną su vynu mes priimam."],["Viešpaties puotos prasmė gili —","Mus, kaip Kūną, reiškia duona,","Ir mes skelbiame visatai:","Kūnas — Tavo meilės išraiška."],["Viešpaties puota tokia gaivi,","Pasisotinam visi čia.","Po jos poreikių nelieka —","Atgaivina Viešpaties puota."],["Žodžiai tie „Kol Jis pas mus sugrįš“ —","Pažadas, kuris mums duotas,","Per dienas jis teikia viltį —","Taip stiprybę semiam šioj puotoj."]],chorus:["Aleliuja, aleliuja! Mes su Juo puotaujame.","Aleliuja, aleliuja! Štai šlovinga Viešpaties puota."]},{number:30,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PRISIMINIMAS",languages:{russian:163,english:221},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/221","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0221_full.mp3",verses:[["Ir už duoną, ir už vyną Viešpačiui dėkojame.","Pasimėgavimas mūsų ši puota Dievo duota.","Duona ši tai Tavo kūnas — atiduotas Tavo mums.","Geriam vyną — Tavo kraują,","Tu praliejai jį už mus."],["Mirtimi Tu atpirkai mus, kad gyvybę perduotum.","Dovanoji Pats Save mums, kad Tave dalintume.","Duoną su taure priėmę Tavo mirtį garsinam.","Ir prisimenam mes Tave","Meiles kupinom širdim."],["Mes šventieji laužom duoną — Kūną slaptą Viešpaties.","Ir tarpusavy bendraujam, Jis suvienijo išties.","Ir palaiminimo taurę mes palaiminsim dabar.","Dalininkai Tavo kraujo","Mes tikėjime vienam."],["Tu per amžius mūs likimas, mes dabar jau džiaugiamės.","Karalystės Tavo laukiam, ir Tavęs sugrįžtančio.","Ir su tais, kur nugalėjo, atsiversi šlovėje.","Mes kaip Nuotaka ateisim","Švęsti Tavo puotoje."]],chorus:["Ši puota šventa mums, su ženklais šventaisiais,","Jų reikšmė šiandieną mums — neištiriama."]},{number:31,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PRISIMINIMAS",languages:{russian:165,english:223},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/223","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0223_full.mp3",verses:[["Meilės puotoje Tavoj matom duoną, taurę čia.","Ir priimdami mes tai — Tavimi pagirdyti.","Už šią taurę duotą mums,","Mes dėkingi esame.","Atpirkimas joj apstus,","Tai palaiminimas mums."],["Tu praliejai kraują šį, kad nuplautum nuodėmes.","Regim pažadą visi — Dievo laiminami mes.","Ir rūstybės taurę šią,","Juk išgėrei Tu už mus.","Geriame palaimą mes,","Nes ją suteikei Tu mums."],["Dievas, buvęs tolimu, tapo mūsų likimu.","Per pralietą kraują Jo — Dievas tapo mums viskuo.","Jo gyvybę turime,","Išpirkti — ramybėj mes.","Dievas turi tikslą mums:","Mes su Juo — dalia Jis mūs."],["Dievo taurė mums duota, kad visi ragautų ją.","Mėgautumės visada dangiškąja dovana.","Dvasioje dalinsimės","Kas atlikta Kryžiumi.","Geriam taurę meilėje","Ir prisimenam Tave."]]},{number:32,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PRISIMINIMAS",languages:{russian:173,english:1107},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1107","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0017_i.mp3",verses:[["Tu surinkai mus, Viešpatie, kaip Kūną,","Nors mūsų daug, bet viena esame.","Mes Jo nariai, gyvybę Jo dalinam,","Mes esam viena Jo gyvybėje."],["Mes Tavo Kūną matom šitoj duonoj,","Sulaužytoj, kad valgytume ją.","Ir imdami kiekvienas savo dalį","Vienybę savo taip paskelbiame."],["Duoną ir taurę prieš save mes regim,","Per Kristaus mirtį tapom viena mes.","Dėl Kūno Jis įvykdė atpirkimą.","Mes viena, Viešpatie, Tavy — Amen!"],["Visi kaip vienas, neišskiriami mes.","Vienybė mūsų tiktai Kristuje.","Mes valgom duoną, geriame iš taurės,","Vienybę mūsų mato visi čia."],["Koks džiaugsmas mes palaimintoj vienybėj,","Ir žinom, Tu patenkintas esi.","Kaip Nuotaka susitikimo laukiam,","Saldžia palaima mes pagirdyti."]],chorus:["Tik vienas Kūnas šioj Visatoje","Ir jis per mus išreiškiamas.","Mes esam viena kiekvienam mieste,","Kad visa tai būt matoma."]},{number:33,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PRISIMINIMAS",languages:{russian:170,english:223},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/233","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0233_full.mp3",verses:[["Koks šis stebuklas, Viešpatie!","Mes viena amžiams su Tavim.","Tu manyje ir aš Tavy,","O, paslaptis didinga ši."],["Atidavei Save už mus,","Kad taptume visi dalis Tavęs.","Praliejai kraują Tu už mus visus,","Išlaisvinai nuo nuodėmės."],["Prisikėlimu pakeitei","Save ir kaip Dvasia grįžai pas mus,","Kad būčiau aš pripildytas Tavim","Ir kad įgyčiau tuos turtus."],["Žiūrėdami į ženklus šiuos,","Mes matom Tavo meilė išreikšta.","Už viską Tau dėkingi esame —","Tavus darbus prisimenam."],["Duoną ir taurę priimam:","Saldumas Tavo pakerėjo mus.","Tik dvasioje sutinkame Tave,","Ir tik Tu ją aprūpini."],["Valgyt ir gerti iš Tavęs,","Tave per dvasią norime priimt.","Pripildyki visus mus savimi —","Tave mes norim prisimint."]]},{number:34,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"BENDRAS HIMNAS",languages:{russian:180,english:241},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/241","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0241_i.mp3",verses:[["Gyrius, garbė ir šlovė visa Tau,","Šlovė visa Tau, šlovė visa Tau.","Gyrius, garbė ir šlovė visa Tau —","Dabar ir amžinai!"],["Gyrius, garbė ir šlovė visa Tau,","Šlovė visa Tau, šlovė visa Tau.","Gyrius, garbė ir šlovė visa Tau —","Dabar ir amžinai!"]],chorus:["Girkite, girkit visi šventieji, girkit.","Girkite, girkit — dabar ir amžinai!","Aleliuja!"]},{number:35,title:"VIEŠPATĮ AŠ GARBINU","sub-title":"",languages:{russian:1,english:1},"hymnal.net-url":"","melody-url":"",verses:[["Viešpatį aš garbinu,","Man patinka Jam giedoti.","Dėl Jo džiaugiuos gyvenimu,","Jis mus atėjo išvaduoti.",`
`,"Tu atėjai iš dangaus parodyt kelią","Ir ant kryžiaus sumokėjai Tu mano skolą,","Ėjai nuo kryžiaus į kapą, o iš jo į dangų.","Viešpatį aš garbinu."]]},{number:36,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO GROŽIS",verses:[["Jėzau, Tu nuostabus,","Jėzau, Tu nuostabus.","Tu Tas, kurį aš myliu,","Tu Tas, kuriam aš noriu atsiduot —","Jėzau, Tu nuostabus."]]},{number:37,title:"DVASIOS PILNATVĖ","sub-title ":"KAIP GYVASIS VANDUO",languages:{russian:192,english:251},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/251","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0251_i.mp3",verses:[["Nuo sosto teka mums upės","Tai Jo gyvasis vanduo.","Viešpats mums siunčia palaimą,","Mėgaujamės mes tik Juo."],["Jeigu tu trokšti ateiki:","Atsargos Jo nesibaigs.","Mums šis vanduo gyvasis","Duodamas čia dovanai."],["O, išvalyki mus, Jėzau,","Nešvarą norim palikt.","Palaimingiausiu kanalu","Tu mus visus padaryk."],["Ištuštink mus ir pripildyk.","Norim naudingais Tau būt.","Pasitikėt Tavimi tik","Ir nuolatos Tau paklust."],["Ir tik tada Tavo upės","Nuolat tekės iš manęs,","Ir žmonės Tavo pilnatvę","Matys nuolatos per mane."],["Lai tik tarnavimas Jėzui","Noru didžiausiu man bus.","Tik Tavimi pasikliauju,","Trokštu su Tavimi būt."]],chorus:["Gyvybę teikiančios upės.","Liejas plačiai laisvėje.","Viešpatie, noriu be galo,","Kad lietus jos per mane!"]},{number:38,title:"DVASIOS PILNATVĖ","sub-title":"KVĖPAVIMAS DVASIA",languages:{russian:194,english:255},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/255","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0255_full.mp3",verses:[["Išmokink mane, o Dieve,","Kad kvėpuočiau Tavimi.","Ir padėk manai esybei","Susiliet su Tavimi."],["Savo gyvastį iškvėpsiu,","Kad pripildytum naujai.","Mano negalia ir jėgos","Te užleidžia vietą jai."],["Visas nuodėmes iškvėpsiu —","Tu prisiėmei ir jas.","Apsivalymą įkvėpsiu —","Gyvenu tik Tavyje."],["Visą vargą aš iškvėpsiu,","Būnant Tavo glėbyje.","Ramų poilsį įkvėpsiu","Džiaugsmą, meilę Tavyje."],["Aš ligas visas iškvėpsiu —","Tu prisiėmei ir jas,","Ir išgydymą įkvėpsiu,","Juk išgydei per žaizdas."],["Norus aš visus iškvėpsiu,","Išklausyki Tu manęs.","Ir atsakymus įkvėpsiu,","Baimėj nepalik manęs."],["Gyvenu Tavim, o Dieve,","Tavo gyvastį geriu.","Vis alsuoju ir alsuoju.","Tik Tavim aš gyvenu."]],chorus:["Nuodėmes visas iškvėpsiu","Ir savas bėdas,","Ir įkvėpsiu, ir įkvėpsiu","Jo pilnatvę aš."]},{number:39,title:"DVASIOS PILNATVĖ","sub-title":"KAIP DVASIA MŪSŲ VIDUJE",languages:{russian:183,english:1113},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1113","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1113_full.mp3",verses:[["Štai Trivienis Dievas mūsų viduje,","Jis atėjo, kad būt mumyse.","Susilieję esam mes su Jo Dvasia,","Mums Jis teikia gyvybę Joje."],["„Abba Tėve“ — šaukiame mes iš gelmių,","Tai iš dvasios, kuri mumyse.","Tai Sūnaus Dvasia, Ji šaukia mumyse,","Mums Ji teikia gyvybę Jame."],["Jėzus Kristus Viešpats mūsų dvasioje —","Kaip Dvasia nuostabi mumyse.","Jis pasikeitė, kad būtų viduje,","Tam kad teiktų gyvybę Dvasia."],["Reali Dvasia — Ji mūsų viduje,","Kaip Dvasia nuostabi mumyse.","Kristaus realumą perduoda Ji mums,","Nes gyvybę mes gaunam per Ją."],["Mes sužadinsim šią dovaną dabar —","Kaip Dvasia Ji yra mumyse.","Šauksim: „Viešpatie“ ir mūs atgis dvasia","Su Dvasia Jo, kuri mumyse."]],chorus:["O, Jis Dvasia nuostabi mumyse,","Dvasia nuostabi mumyse.","Tėvas Sūnuje, Sūnus dabar Dvasia —","Jis Dvasia nuostabi mumyse."]},{number:40,title:"IŠGELBĖJIMO UŽTIKRINTUMAS IR DŽIAUGSMAS","sub-title":"TEISUMAS KRISTUJE",languages:{russian:224,english:298},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/298","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0298_i.mp3",verses:[["Pasitikiu tik Kristumi","Per amžius Jis man atrama.","Tiktai Jame esu teisus,","Kitų nepripažįstu aš."],["Jo veidas miglą išsklaidys.","Audros bangų jau nebeliks.","Laikausi aš malonės Jo,","Jo — savo dvasios inkaro."],["Jo švento kraujo sandora","Vis saugo mane bėdose.","Nors gali griūt aplink mane,","Aš atramą randu Jame."],["Ir kai sugrįš Jis šlovėje,","Te ras mane Jis teisume.","Su baltu ir šventu rūbu,","Kad aš su Juo galėčiau būt."]],chorus:["Ant Kristaus stoviu aš — Uolos,","Nes visa kita — smėlis tik.","Nes visa kita — smėlis tik."]},{number:41,title:"IŠGELBĖJIMO UŽTIKRINTUMAS IR DŽIAUGSMAS","sub-title":"IŠGELBĖTI JO MALONE",languages:{russian:240,english:313},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/313","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0313_full.mp3",verses:[["O, nuostabi malonė","Išgelbėjo mane.","Buvau apakęs, be vilties,","Dabar džiaugiuos šviesa!"],["Širdy paraginai mane,","Sutvirtinai drąsa.","Kokia buvai brangi, miela,","Kai priėmiau tave!"],["O Viešpatie, Tu geras man —","Viltis Tavy saugi.","Tu mano skydas ir dalis","Gyvenimo kely!"],["Daugybė amžių nutekės","Tavos šlovės sraute,","Nebus daugiau tamsių dienų —","Širdy turiu Tave!"]]},{number:42,title:"IŠGELBĖJIMO UŽTIKRINTUMAS IR DŽIAUGSMAS","sub-title":"PASITENKINIMAS KRISTUMI",languages:{russian:245,english:322},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/322","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0322_i.mp3",verses:[["Šitiek metų aš ieškojau šios versmės,","Amžinosios jos tėkmės.","Ką buvau suradęs šioje žemėje —","Nebuvau palaimoje."],["Nebenoriu klaidžioti aš dykumoj —","Atradau palaimą aš.","Amžina malonė Dievo man duota —","Gyvenu aš Kristuje."],["Štai kur džiaugsmas ir gyvenimo prasmė,","Poilsiaujame Jame.","Piktos dienos nebebaisios — patikėk,","Mes malonę turime."],["Tikrą viltį aš turiu tik Kristuje,","Taip lieku stiprybėje.","Gydantis šaltinis sielai tik Jame,","Taip išlieku šviesoje."]],chorus:["Turim amžiną Šaltinį mes šiandien,","Geriame visi iš Jo mes kasdien.","Taip atrandam džiaugsmą,","Gerdami palaimą","Iš Šaltinio Dievo duoto mums."]},{number:43,title:"IŠGELBĖJIMO UŽTIKRINTUMAS IR DŽIAUGSMAS","sub-title":"GELBĖJIMASIS NUO PASAULIO",languages:{russian:252,english:1127},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1127","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1127_i.mp3",verses:[["Jis mane išgelbėjo —","Negrįšiu atgal niekada.","Jis mane išgelbėjo —","Negrįšiu atgal niekada."],["Babelę aš palikau —","Negrįšiu atgal niekada","Babelę aš palikau —","Negrįšiu atgal niekada"],["Vietinėj Bažnyčioj aš —","Negrįšiu atgal niekada.","Vietinėj Bažnyčioj aš —","Negrįšiu atgal niekada."],["Viešpatį aš paliečiau —","Negrįšiu atgal niekada","Viešpatį aš paliečiau —","Negrįšiu atgal niekada."],["Viskuo Kristus tapo man —","Negrįšiu atgal niekada,","Viskuo Krislus tapo man —","Negrįšiu atgal niekada."],["Pagaliau turiu namus —","Negrįšiu atgal niekada.","Pagaliau turiu namus —","Negrįšiu atgal niekada."],["Už atstatymą esu —","Negrįšiu atgal niekada.","Už atstatymą esu —","Negrįšiu atgal niekada."]],chorus:["Ne, ne, ne, ne, ne.","Negrįšiu atgal niekada!","Ne, ne, ne, ne, ne.","Negrįšiu atgal niekada!"]},{number:44,title:"KRYŽIAUS KELIAS","sub-title":"GYVYBĖS KELIAS",languages:{russian:456,english:631},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/631","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0441_i.mp3",verses:[["Kad patirt prisikėlimą.","Kryžių aš turiu priimt.","Iš mirties gyvybė kyla.","Kad gyvent — turi numirt."],["Manyje, kad augtų Kristus.","Mano dvasios gilumoj.","Reikia sielą man palikti —","Kryžių nešti nuolatos."],["Jei aš būsiu nukryžiuotas","Dvasioje su Viešpačiu.","Tik tada paveikus mirčiai,","Jo gyvybė ištekės."]],chorus:["Jei nemirsime —","Negyvensime.","Iš mirties gyvybė kyla.","Gimsta šviesoje."]},{number:45,title:"ILGESIAI","sub-title":"AUGIMUI KRISTUJE",languages:{russian:281,english:395},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/395","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0395_full.mp3",verses:[["O Jėzau Kristau, auk many,","Tada mažėsiu aš.","Padėk atverti širdį Tau,","Palikti nuodėmę."],["Tava Šviesa, kur šviečia man","Patrauks piktas mintis.","Kad niekas aš, o viskas Tu —","To mokaus aš kasdien."],["Duok man matyt šlovės daugiau.","Teisus ir šventas Tu.","Malonėj ar varge išlikt","Tavuoju atvaizdu."],["Pripildyk dangišku džiaugsmu,","Laikyk Sava jėga.","Ir Tavo meilei leisk spindėt","Ir liekis į mane."],["Būk man gyvenimo tikslu","Ir auki manyje.","Aš noriu nešti vaisių Tau","Išliek malonę man."]],chorus:["Kasdieną Tu Sava jėga","Apgaubki negalią,","Apšvieski Tu Sava šviesa,","Suteik gyvybę man."]},{number:46,title:"SĄJUNGA SU KRISTUMI","sub-title":"VIENYBĖ SU JUO",languages:{russian:327,english:474},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/474","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0013_i.mp3",verses:[["Viena, Viešpatie, mes esam","Dvasioje su Tavimi.","Ką turi, turiu ir aš, nes","Tu man viską suteiki."],["Tavo žmogišką gyvybę,","Viską Tavo priimu.","Paklusnumas Tavo, Jėzau,","Tapo man prieinamu."],["Mirtyje su Tavim viena.","Aš ant kryžiaus su Tavim.","Šiam pasauliui aš numiręs,","Ir pasaulis miręs man."],["Prisikėlime mes viena.","Prisikėliau Tavyje.","Manyje Tava gyvybė,","Pasikėliau aš per ją."],["Su Tavim išaukštintas aš,","Danguje aš su Tavim.","Žemėje esu praeivis,","Gyvenu tik Tavyje."],["Su Tavim aš karaliauju,","Ir valdau aš su Tavim.","Vienas kitame gyvenam:","Aš Tavy, o Tu many."]],chorus:["Viena mes, viena mes.","Viena mes, viena mes.","Per dienas esu Tavyje,","Skęstu Tavo turtuose."]},{number:47,title:"VIEŠPATIES KŪNAS",verses:[["Viešpaties Kūnas, Jėzaus Kristaus kraujas","Atneša išganymą ir perkeičia tave","Viešpaties Kūnas, Jėzaus Kristaus kraujas","Atneša išganymą ir perkeičia tave","Džiaukis, dėkoki, ši malonė tau duota","Vienytis su Viešpačiu ir pasilikt Jame","Džiaukis, dėkoki, ši malonė tau duota","Vienytis su Viešpačiu ir pasilikt Jame"]]},{number:48,title:"KRISTAUS, KAIP GYVYBĖS, PATYRIMAS",languages:{russian:356,english:507},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/507","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0507_i.mp3",verses:[["Buvau be Dievo tamsoje,","Pavergtas nuodėmės;","Bet Žodis apšvietė mane,","Kad Kristus manyje."],["Šviesa pažadina gėles","Iš sėklos žemėje,","Taip trykšta meilė ir šviesa","Iš Kristaus manyje."],["Gėlės gyvybė sėkloje;","Iš sėklos veržias ji.","Taip Viešpačiui šlovė už tai,","Kad Jo Dvasia many."],["Pripildytas siekimo aš,","Kad tapčiau toks kaip Jis;","Šia mintimi aš gyvenu:","„Kristus gyvena many“."]],chorus:["Kristus, gyvena many,","Kristus gyvena many.","Nuostabus Jo išgelbėjimas —","Kristus gyvena many."]},{number:49,title:"KRISTAUS PATYRIMAS","sub-title":"JO PRIEINAMUMAS",languages:{russian:427,english:539},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/539","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0539_full.mp3",verses:[["Esi gyvybė, Viešpatie,","Tu viskas man esi.","Subjektyvus, prieinamas —","Galiu Tave pažint."],["Tu poreikius patenkini","Savuoju perteklium.","Tavas naudosiu atsargas","Su noru ir džiaugsmu."],["Šis patepimas mumyse","Palaiko silpnume.","Savu energijos srautu","Pripildai Tu mane."],["Įstatymas, kuris širdy,","Tavuoju ves keliu.","Esybę man pripildei","Tu Savu realumu."],["Su Tavim viena amžiams mes —","Vienybei pašaukei.","Ir vieną dvasią turim mes","Dabar ir amžinai."]],chorus:["Esi šiandien Dvasia Tu!","Man artimas esi!","Taip paprastai prieinamas —","Žaviuosi aš Tavim."]},{number:50,title:"KRISTAUS PATYRIMAS","sub-title":"JO TURTAI",languages:{russian:397,english:542},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/542","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0542_i.mp35",verses:[["Mūsų Gelbėtojo turtai","Nieks negali jų ištirt.","Bet šiandien pilnatvę Dievo","Aš apsčiai galiu patirt."],["Kristaus Gelbėtojo turtų","Neįmanoma aprėpt.","Jie pripildyti gyvybe —","Niekad nenustosią šviest."],["Išmintis, valdžia, ramybė","Ir išgydymas juose.","Pasimėgavimas mūsų —","Tiktai Kristaus turtuose."],["Šie turtai — Pats Viešpats Dievas,","Viskas slypi tik Jame.","Jo valdžia ir asmenybė","Šiandien mano dvasioje."],["Mūsų Gelbėtojo turtai —","Nieks negali jų suvokt.","Ilgį, plotį, gylį, aukštį","Nieks negali išmatuot."],["Mūsų Gelbėtojo turtai","Neapsakomi yra,","Bet tik jie man tikras džiaugsmas","Mano amžina jėga."],["Aš veržiuos suvokti turtų —","Kristaus jie pilnatvėje,","Kad galėčiau jais dalintis","Su šventaisiais meilėje."]],chorus:["Kristaus turtai, Kristaus turtai","Skiriami jie man yra,","Nors ir sunku juos ištirti —","Bet realūs visada."]},{number:51,title:"KRISTAUS PATYRIMAS ","sub-title":"SEKIMU DVASIA",languages:{russian:428,english:594},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/594","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0594_i.mp3",verses:[["Tu mana ramybė","Ir gyvybė Tu,","Taip išlieku dvasioj","Aš su Viešpačiu."],["Viešpaties esu jau,","Buvau pančiuose.","Tik per Jo gyvybę","Dabar laisvėje."],["Aš mąstysiu dvasioj,","Kūniškumui — ne!","Dvasiai tik paklūsti","Noriu visada."],["Padeda man Kristus","Dvasiniu išlikt,","Net kūnu gyvybę","Aš galiu patirt."],["Ir mana ir Dievo","Dvasios viena jau:","Paveldėtojas aš,","Aš iš Jo gimiau."]],chorus:["Jo Dvasia seku aš,","Viešpaty esu.","Jis gyvybę teikia.","Džiaugsmą atrandu."]},{number:52,title:"KRISTAUS PATYRIMAS","sub-title":"DVASIOJE",languages:{russian:342,english:1141},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1141","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1141_i.mp3",verses:[["Mes giedosime Viešpačiui dvasioj.","Mes giedosime Jam iš gelmių.","Aleliuja! Sušuksime Jėzui.","Susilieję vienybėj su Juo."],["Daug mes metų religijoj buvom.","Proto valdomi buvome mes,","Jausmuose pasiklydę klajojom.","Nežinojome kur mūs dvasia."],["Mokomės kreipti žvilgsnį į dvasią.","Kaip gyvybę mes priimam Jį.","Mokomės Jėzumi pasisotint.","Ir beprasmišką kovą palikt."],["Jam šlovė už gyvybę Jo Dvasioj.","Jam šlovė už Jo atgaivą mums.","Kreipdamiesi nuo proto į dvasią","Ten atrasime mes Jo tūrius."]]},{number:53,title:"KRISTAUS, KAIP MAISTO IR GĖRIMO, PATYRIMAS",languages:{russian:362,english:1145},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1145","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0791_i.mp3",verses:[["Mums Dievas davė Sūnų čia —","Gyvybės medį dovanai,","Kad paragautume mes Jo,","Jog Dievas maistas mums.","Tai Dievas maistas mums, tai Dievas maistas mums!","Ragavę Jo mes sakome, jog Dievas maistas mums."],["Puotaujam prisipildę Juo,","Tik valgant galim Juo gyvent,","Kad Dievas persunktų Savim","Esybę mus visų.","Tai Jėzus mūs puota, tai Jėzus mūs puota!","Tik valgant galim Juo gyvent, nes Jėzus mūs puota."],["Mūs Jėzus Kristus maistas mums,","Kaip duona kasdieninė.","Mes žinom: atsargos Jame","Kasdienai mums visiems.","Kasdien Jis duona mums, kasdien Jis duona mums!","Mes žinom atsargos Jame, kasdienai mums visiems."],["Jis toks prieinamas dabar.","Puota ši teikia džiaugsmą man.","„Kas trokšta — Dievas sako tam:","Eik gerti dovanai.“","Eik gerti dovanai, eik gerti dovanai!","„Kas trokšta — Dievas sako tam: eik gerti dovanai!“"]]},{number:54,title:"KRISTAUS, KAIP VALGIO IR GĖRIMO, PATYRIMAS",languages:{russian:366,english:1150},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1150","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1069_i.mp3",verses:[["Gyvybės duoną valgome,","O iš šaltinio geriame.","Ir Jėzus sako: gerk iš Jo,","Tada netrokši niekada!","Kaip, netrokšiu niekada?","Taip, netrokši niekada.","Kaip, netrokšiu niekada?","Taip, netrokši niekada!","Ir Jėzus sako: gerk iš Jo,","Tada netrokši niekada!"],["Gyvybės duoną valgome,","Su Viešpačiu puotaujame.","Gyvybės duoną valgyk tu,","Tada nealksi niekada.","Kaip, nealksiu niekada?","Taip, nealksi niekada!","Kaip, nealksiu niekada?","Taip, nealksi niekada!","Gyvybės duoną valgyk tu,","Tada nealksi niekada."],["Kvėpuojame gyvybe mes,","Taip prisipildom Viešpaties.","Ir kas kvėpuos gyvybe šia,","Gyvybė Dievo bus jame.","Kaip, gyvybė bus jame?","Taip, gyvybė bus jame!","Kaip, gyvybė bus jame?","Taip, gyvybė bus jame!","Ir kas kvėpuos gyvybe šia,","Gyvybė Dievo bus jame."],["Gyvybės vyną geriame,","Kuris yra mūs dvasioje.","Ir kas tik geria Jį visad,","Per amžius džiaugsis jis Dieve.","Kaip, džiaugsis jis Dieve?","Taip, džiaugsis jis Dieve!","Kaip, džiaugsis jis Dieve?","Taip, džiaugsis jis Dieve!","Ir kas tik geria Jį visad,","Per amžius džiaugsis jis Dieve."],["Mūs Jėzus, prisikėlęs Jis,","Kad gyvybę į mus dalint.","Ir jeigu tu Jį priėmei.","Gyvensi tu jau amžinai.","Kaip, gyvensiu amžinai?","Taip, gyvensi amžinai!","Kaip, gyvensiu amžinai?","Taip. gyvensi amžinai!","Ir jeigu tu Jį priėmei —","Gyvensi amžinai Jame."]]},{number:55,title:"KRISTAUS, KAIP VALGIO IR GĖRIMO, PATYRIMAS",languages:{russian:367,english:1151},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1151","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1151_i.mp3",verses:[["Teka mums nuo sosto upė gyvojo vandens,","Valgyt nuo gyvybės medžio nėr mums maloniau.","Tu pažvelk! Nereiks jau saulės, nei mėnulio mums;","Nes nakties nebus!"],["Iš gelmių kaip upė teka Kristus mums visiems.","Jis, kaip medis, duodantis saldžius vaisius kasdien.","Jis šviesa mums ir diena mūs, ir žvaigždė Jis mūs —","Viskas Kristus mums!"],["Kad priimt gyvybę, balinsim mes drabužius.","„O, Amen ir Aleliuja“ — šauksim su džiaugsmu.","Kad pažinti Kristų, dvasią mes mankštiname;","Kristus — lobis mūs!"],["Turime namus, kad saulę net atstoja mums.","Ir juose suvienijo kaip brolius Viešpats mus.","Čia visus mus Jis surinko, kad išreikšti Jį","Jo bažnyčioje!"]],chorus:["Ir Dvasia, ir Nuotaka Jam sako: Tu ateik.","Ir visi, kurie tai girdi, lai kartoja tai.","Ir kas trokšta, te ateina gerti dovanai","Gyvojo vandens!"]},{number:56,title:"KRISTAUS PATYRIMAS","sub-title":"MĖGAVIMASIS JUO",languages:{russian:425,english:1152},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1152","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1152_i.mp3",verses:[["Vilioja Kristus mus visus kasdieną vis stipriau —","Tyresnio ir skaistesnio mes nerasime daugiau.","Nėra mums žodžių išsakyt — nėra aukščiau už Jį;","Kiekvieną dieną vis brangesnis darosi mums Jis."],["Atėjus į bažnyčią Kristų mes išvydome,","Ir Jo gausi malonė išsiliejo šlovėje.","Atradome pilnatvę mes, atradom Jo turtus;","Kiekvieną dieną vis brangesnis darosi Jis mums."],["Gyvybę teikianti Dvasia — Ji mūsų dvasioje.","Ji taip visus augina mus Savoj gyvybėje.","Susirinkimuos su Šventaisiais kyla mūs dvasia,","Ir Kristus tapo mums visiems brangesnis dieną šią."],["Atstatymui sujungė Jis visus mus Savimi,","Kad Kristų ir Bažnyčią mes regėtume visi.","Ką turime, kas esame dalijamės šiandien;","Dėl Dievo tikslo mes, kuris brangesnis vis kasdien."]],chorus:["Negalim išsakyti, kas slypi mūsų Kristuje —","Jis lobis mūs brangiausias ir brangėja Jis kasdien."]},{number:57,title:"KRISTAUS PATYRIMAS","sub-title":"MŪSŲ PASIMĖGAVIMAS",languages:{russian:426,english:1153},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1153","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1153_i.mp3",verses:[["Mes suradom Kristų, Jis dabar","Tapo viskas visame.","Vardo Jo kasdien mes šaukiamės —","Taip palaimoj esame."],["Radom Kristų — Jis dabar Dvasia,","Jis gyvena mumyse.","Toks prieinamas yra Jis mums —","Nuolat mūsų širdyse."],["Mes suradom kelią Juo gyvent:","Jo žodžiu mūs maldoje.","Jo tik šaukiamės mes nuolatos,","Valgom, geriam mes Jame."],["Vietinę bažnyčią radome —","Jau namus mes turime.","Mums atsakymai bažnyčioje","Kaip palikti Babelę."],["Džiaugsmui šiam nėra jokių ribų","Mūs susirinkimuose.","Viešpats duoda mums šiandien jėgų,","Jo turtuose skęstame."]],chorus:["Šiuo džiaugsmu mes džiaugiamės kiekvieną dieną.","Dievo džiaugsmas! Dievo džiaugsmas!","Šiuo džiaugsmu mes džiaugiamės kiekvieną dieną —","Nieks negali mums jo apribot."]},{number:58,title:"ŠIRDYJE GIEDU GIESMĘ",verses:[["Širdyje giedu giesmę","Tam, kuris mirė už mane.","Viešpatie, trokštu būt šalia,","Nes ištikima meilė Tava.","Džiaugsmo versmė!","Gyvybės srovė! Tu visa man esi!","Širdis priklauso Tau vienam.","Gyvenimą atiduodu Tau,","Argi kitaip atsidėkot galiu? (2k.)"],["Kai tądien sutikau Tave","Aš paskendau meilės jūroje.","Manyje buvo vien tamsa.","Kol ją išsklaidė Viešpaties šviesa.","Kai atsigręžiau,","Atvėriau širdį Tau.","Tu įėjai vidun, pripildei visas gelmes!","Dabar žinau — esame kartu","Ir mūsų nebeišskirs niekas per amžius! (2k.)"]]},{number:59,title:"KRISTAUS PATYRIMAS","sub-title":"MEILĖ JAM",languages:{russian:403,english:1159},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1159","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0713_i.mp3",verses:[["Jėzau, sužavėtas Tavo grožiu.","Atveriu aš savo širdį Tau.","Pasilikt religijoj nenoriu,","Noriu priklausyti aš tik Tau.","Kai visad žvelgiu į Tavo šlovę,","Pradedu tada ir aš spindėt.","Aš maldauju: Jėzau, mane persunk,","Savo Dvasią su mana suliek."],["Sušvytėjęs! Regiu aukštumose","Tavo sostą, o Žmogaus Sūnau!","Dievo ugnimi esu apgaubtas,","Kad su Tavimi spindėčiau.","Ir pažindamas puikumą Tavo,","Gėdoje prarandu meilę sau.","Tiktai Tau mes atiduodam šlovę,","Už Tave mums nieko nėr saldžiau."],["Indą alebastrinį, o Jėzau!","Sudaužysiu aš iš meilės Tau.","Ir patepsiu galvą Tau, Valdove,","Pamiltasis, dovana ši Tau.","Dėl Tavęs aukojuos šiandien, Jėzau!","Tave myliu — tuo laimingas aš.","Savo širdyje aš meilės pilnas,","Brangų tepalą atnešiu aš."],["O, pakilk ant smilkinimo kalno,","Aš ilgiuosi, Viešpatie, Tavęs.","Iš širdies šaltinio gerki, Jėzau!","Atrandu ramybę Tavyje.","Ir ne vienas dievinu Tave aš:","Su šventaisiais — Tavo Nuotaka.","O, ateik! Tavęs mes, Jėzau, laukiam;","Norim būti Tavo meilėje."]]},{number:60,title:"PASIŠVENTIMAS","sub-title":"PATRAUKTI VIEŠPATIES GROŽIU",languages:{russian:308,english:437},"hymnal.net-url":"https://www.hymnal.net/en/hymn/nt/437","melody-url":"https://www.hymnal.net/Hymns/NewTunes/mp3/e0437_new.mp3",verses:[["Kas tiktai pamato Jėzų,","Atiduoda širdį Jam.","Nuolatos mes su džiaugsmu,","Giedame tik Jam vienam."],["Apgauti stabų tuštybės","Veltui kreipėmės į juos.","Nes juose nėra gyvybės,","Vien tik klystkeliai tamsos."],["Žemiški stabai neteko","Apgaulingos prabangos,","Kai pražydo Jo pilnatvė","Neprilygstamos šviesos."],["Netikrų dievų vilionės","Išsisklaidė lyg naktis,","Kai nušvito Jėzaus grožis","Atsivėrė Jo širdis."],["Baigėsi nakties tamsybė,","Kyla saulė — jau aušra.","Baigėsi žiemos galybė,","Mus užliejo šiluma."],["Trokštu Jam atsidėkoti,","Jėzaus veidas — mūs viltis.","Su mumis kartu raudoti","Sugeba tik Jo širdis."],["Atgaivinti Jo gyvybe","Ir pripildyti džiaugsmu,","Apgaubti mes Jėzaus meile —","Nebėra daugiau stabų."]],chorus:["Pavergti mes Tavo grožio","Gyriaus duoklę nešam Tau.","Neprilygstamas, Valdove,","Nuostabus, Karaliau!"]},{number:61,title:"KRISTAUS PATYRIMAS","sub-title":" BENDRAVIMAS SU JUO",languages:{russian:405,english:549},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/549","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0454_i.mp3",verses:[["Uždangą pereik, stovyklą palik.","Dangų ragavęs, žemę tu pamiršk.","Jeigu aš dvasioj pasisotinu,","Jau negaliu aš eit sielos keliu."],["Uždangą pereik, stovyklą palik.","Žemė nublanksta prieš dangaus turtus.","Jei tavo dvasią pavergė dangus,","Tu jau paliksi žemiškus džiaugsmus."],["Uždangą pereik, į Kristų pažvelk.","Palik stovyklą, tegu Jėzus ves.","Sieki vainiko tu sava dvasia,","Te kryžius netrukdys Jo vedime."],["Palik stovyklą, kryžių neški tu.","Prisikėlimo jėgą priimki.","Jeigu danguj regėsi veidą Jo,","Čia, žemėje galėsi sekti Juo."],["Uždangą pereik, ateiki puotaut.","Palik stovyklą, vargo nebijok.","Nors žemėje blaškaus aš širdimi,","Bendravime mana dvasia giedos."],["Uždangą pereik, Kristų tu ragauk.","Palik stovyklą, reik tavęs žmonėms.","Dievas gyvybę Savo nori liet","Per tave Savo išrinktiems žmonėms."],["Uždangą pereik, stovyklą palik,","Kol jie ateis į amžinybę Jo.","Kai bus vienybėj žemė ir dangus,","Dievas tada su žmogum viena bus."]]},{number:62,title:"KRISTAUS PATYRIMAS","sub-title":"JO IŠRAIŠKA",languages:{russian:424,english:591},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/591","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0591_i.mp3",verses:[["Ne, ne mane! Many temato Kristų!","Kristų tegirdi, gerbia, myli Jį.","Kristus — ne aš, mani veiksmai ir žodžiai,","Kristus — ne aš, mintyse ir darbuos."],["Kristus — ne aš, išsklaido visas baimes","Ir sielvartus, taip slegiančius mane.","Kristus — ne aš, Savu žodžiu paguodžia,","Kristus — ne aš, nušluosto ašaras."],["Kristus tiktai! Aplinkui vien tuštybė!","Kristus tiktai! Mūs žodžiai laikini.","Kristus tiktai. Mano planai beprasmiai!","Te iš manęs nė pėdsako neliks."],["Kristus — ne aš, varguos visus paguodžia.","Kristus ne aš, mus jėgos tik Jame.","Kristus tiktai mūs sielai, dvasiai, kūnui.","Kristus tiktai gyvena manyje."],["Kristų tiktai nuo šiol visur regėsiu.","Greit pamatysim mes Jį šlovėje.","Kristus, tik Jis, išpildo mano norus.","Kristus, tik Jis, man viskas visame."]],chorus:["Noriu pabėgti aš nuo savęs,","Tavyje pasislėpt.","O, kad gyvenčiau jau ne aš,","O Tu, mūs Viešpatie Kristau many."]},{number:63,title:"DIEVO, KAIP MŪSŲ AMŽINOJO LIKIMO, PATYRIMAS",languages:{russian:436,english:600},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/600","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0177_i.mp3",verses:[["Dalia mus Dievas, meilė Jis.","Visur Tu visada.","Be Tavęs viskas tuštuma","Danguj ir žemėje,","Danguj ir žemėje."],["Tušti net dangūs be Tavęs.","Vien dulkės žemėje.","Tik Tavęs, Dieve, trokštame —","Kitaip tuštybėj mes,","Kitaip tuštybėj mes."],["Aš Tau dėkoju, Viešpatie,","Davei gyvenimą.","Namus, draugus, turtus davei —","Tai neatstos Tavęs,","Tai neatstos Tavęs."],["Turtai visi — tuštybė tai!","Tai neprilygsta Tau!","Net pinigai, geri draugai","Ir žemiški džiaugsmai,","Ir žemiški džiaugsmai."],["Nors žemė priklausytų man","Ir žvaigždės danguje,","Bet be malonės ir Tavęs","Esu tuštybėje,","Esu tuštybėje."]]},{number:64,title:"DIEVO, KAIP GYVYBĖS, PATYRIMAS",languages:{russian:438,english:1191},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1191","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1191_full.mp3",verses:[["Iš gelmių dvasioje teka mums atgaiva","Tai Viešpats mūs Dievas brangus.","Tėvas — mūsų versmė, Kristus tapo vaga,","Ir gyvybę man teikia Dvasia."],["Žaliose lankose Viešpats gano mane,","Prie tylių vandenų veda Jis.","Nei kovų, nei aistrų — tušti mano keliai,","Tik tas srautas gyvybę man teiks."],["Šventų Švenčiausioj gyvybę dalint,","Mane Tu, o Jėzau, šauki.","Aleliuja! Girdžiu balsą raginantį:","„Šaka vynmedžio lik tu Many“"]],chorus:["Branginu srautą aš gyvąjį,","Neklausysiu aš sielos audrų.","Viešpatie, gilink srautą many,","Te gyvybė man bus vainiku."]},{number:65,title:"DIEVO KAIP GYVYBĖS, PATYRIMAS",languages:{russian:441,english:1196},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1196","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1196_i.mp3",verses:[["Mūs Dievas gyvas! O, Aleliuja!","Ir mumyse Jis! O, aleliuja!","Jį paragavom,","Dalia Jis mūsų.","Ir šaukiamės Jo nuolatos."],["Visi kartu mes! Kaip nuostabu tai!","Nes viena dvasioj šeima mes tapom.","Jo Kūnas esam","Ir Jį išreiškiam,","Mums Jėzus tapo realus."],["Bažnyčios visos Juo prisipildė,","Nes nori, kad Jį pažintų žmonės.","Su Juo į miestus","Mes norim eiti,","Ir taip apreikšti Jį visiems."]]},{number:66,title:"DIEVO PATYRIMAS","sub-title":"PER TREJYBĘ",languages:{russian:443,english:608},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1196","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0608_full.mp3",verses:[["Sūnus ir Tėvas, ir Dvasia Šventoji —","Esybėj viena esą visi Trys.","Viskuo mums tapti Dievas šiandien nori,","Per Sūnų Dvasioj nori mus įgyt."],["Šaltinis — Tėvas, iš Jo teka turtai,","Visus juos nori Jis mums atiduot.","Jame mes atsargas visi atrandam,","Ir trokštame visi Jam atsiduot."],["Kaip nuostabu! Sūnus išreiškė Dievą,","Gyvendamas čia žemėj tarp žmonių.","Iš nuodėmės visus Jis mus atpirko,","Sutaikė nuodėmingus su Dievu."],["Priėmęs kryžių, Jėzus Dvasia tapo.","Jo tikslas Dievą perduoti į mus.","Šlovinga tai, kad dvasios viena tapo,","Ir visą amžinybę viena bus."],["Kaip realu, mūs Dievas Dvasia šiandien!","Gyvenkim Juo ir lieskime tik Jį!","Ir viena su Dievu mes esam dvasioj,","Ir nieks negali mūsų jau atskirt."]],chorus:["Trivienis Dievas Tapo mums viskuo!","Šlovinga tai ir nuostabu!","Ir visa tai mes gavom dovanai!","Kaip nuostabu, šlovinga tai!"]},{number:67,title:"DIEVO PATYRIMAS","sub-title":"PER DVASIOS NAUDOJIMĄ",languages:{russian:447,english:612},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/612","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0612_full.mp3",verses:[["Dievas nori mus maitinti","Esybe Sava,","Kad su Juo mes būtum viena","Savo dvasiose."],["Dvasioje, palietę Dievą,","Mes supratome.","Prigimtį Jis Savo davė","Mums Sava Dvasia."],["Dievą liečiame mes dvasioj,","Suvokiam protu.","Bet jei dvasios nenaudosiu","Liksiu aš vargšu"],["Mes malda priimsim Žodį","Tavo gyvąjį.","Taip visus mus Jis pripildys","Savimi."],["Likdami mes Dievo Žody","Liečiam Viešpatį.","Bet vis tiek mums reikia dvasios,","Raidėje — mirtis."],["Mums per Dvasią spinduliuoja","Jo šlovės turtai.","Savo dvasią mes naudosim","Ir įgysim tai."]],chorus:["Dievo mums esybė liejas","Dvasios tėkmėje.","Tik naudodami mes dvasią","Jį pažinsime."]},{number:68,title:"PARAGINIMAS BĖGTI LENKTYNĖSE",languages:{russian:471,english:1206},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1206","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1206_i.mp3",verses:[["Štai lenktynės skirtos mums — Aleliuja!","Turim bėgti šiuo keliu pirmyn.","Paskui tuos, kas bėga jau — Aleliuja!","Dievas šakė mums: „Žiūrėk į Jį“."],["Viską nuo savęs atmesk — Aleliuja!","Nusisuk nuo triukšmo ir kovų.","Būsi tik Jame ramus — Aleliuja!","Tik pažvelk, pažvelk tu į Jį."],["Nuo audringos sielos tu — Aleliuja!","Nusisuk nuo savo nesėkmių.","Tu į tikslą pažiūrėk — Aleliuja!","Ir pažvelk, pažvelk tu į Jį."],["Visą praeitį atmesk — Aleliuja!","Gėrį su blogiu paliki tu.","Ir laikykis tu tik Jo — Aleliuja!","Ir pažvelk, pažvelk tu į Jį."],["Pažiūrėk į veidą Jo — Aleliuja!","Tavo kelią tik Jisai užbaigs.","Gausime malonę mes — Aleliuja!","Jeigu žvelgsime tiktai į Jį."]],chorus:["O, pažvelk, o pažvelk,","Tu į Jėzų pažiūrėk.","Ir nuo visko atsisuk tu į Jėzų","Ir pažvelk, pažvelk tu į Jį."]},{number:69,title:"VIDINĖS GYVYBĖS SKIRTINGOS PUSĖS","sub-title":"DVI DVASIOS KAIP VIENA",languages:{russian:502,english:745},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/745","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0745_i.mp3",verses:[["O, mano Viešpats — Jis Dvasia,","Jis mano dvasioje.","Dvi dvasios tapo kaip viena,","Kad būt vienybėje."],["Per Tavo Dvasią supratau","Aš savo dvasioje,","Kad aš tapau Tavu vaiku","Ir turiu paveldą."],["Tik dvasioje liečiu Tave,","Noriu Tave pažint,","Tu kaip Dvasia mums atskleidi","Savąją prigimtį."],["Tik dvasioje mes galime","Eiti Tavu keliu.","Tu kaip Dvasia jį apšvieti,","Kad eitume kartu."],["Tik per Tave mes dvasioje","Galime Tau tarnaut,","Per Savo Dvasią mumyse","Duodi Save ragaut."],["Tik per Tave mūs dvasioje","Maldos pakyla Tau,","Tu kaip Dvasia teki per mus —","Nebesustabdomai."],["Kad susitikti su Tavim,","Grįžtam į dvasią mes.","Tiktai joje dalijamės","Tėvo palikimu."],["Kokia vienybė. Viešpatie,","Dvi dvasios jau viena.","Tavo Dvasia — Ji manyje,","O mano Tavyje."]]},{number:70,title:"SKIRTINGI VIDINĖS GYVYBĖS ASPEKTAI","sub-title":"TRANSFORMACIJA",languages:{russian:507,english:750},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/750","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0750_full.mp3",verses:[["Dievas nori atmainyti","Mus į atvaizdą Sūnaus.","Darbą turi šį atlikti","Jo Dvasia mums iš vidaus."],["Gimėme visi iš Dievo,","Jo Dvasia atgimdė mus.","Atstatyki, Dieve, sielas —","Norim tik Tavyje būt."],["Skverbsis Jo Dvasia į sielą,","Ir ji atmainyta bus.","Viską viduje atnaujins,","Tada būsiu paklusnus."],["Atmainys Sava Jėga mus,","Tapsim atvaizdu Sūnaus.","Iš šlovės į šlovę ves mus,","Kad mums visa tai įgaut."],["Keisimės mes ir šventėsim,","Augsime visi Jame;","Iki tol, kol visi būsim","Kristaus mes pilnatvėje."]],chorus:["Savu atvaizdu daryki","Mūs prote ir jausmuose.","Persunki Sava Dvasia mus,","Norim būt pilnatvėje."]},{number:71,title:"MALDA","sub-title":"DVASIOS NAUDOJIMAS",languages:{russian:526,english:782},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/782","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0782_i.mp3",verses:[["Paslaptinga, Viešpatie,","Kad many Tu gyveni.","Koks stebuklas šis yra,","Kad mes viena Tavyje."],["Dvasioje galiu gyvent,","Nuolatos joj vaikščioti.","Tau dvasia galiu tarnaut,","Dvasioj Tave aukštinti."],["Meldžiantis Tavu žodžiu","Dvasioje Tave liečiu","Ši malda sustiprina,","Mano dvasią pakelia."],["Dvasią stiprink man — meldžiu,","Kad kitus pažadinčiau.","Te pakils mana dvasia,","Kad atverčiau jiems Tave."],["Visuomet kai aš kalbu,","Te dvasia mana kalbės.","Ir ką bedaryčiau aš,","Lai tik veda ji išties."],["Kai aš būnu dvasioje","Ir ji liejas per mane,","Te atvers kitų dvasias","Ir nuneš juos pas Tave."],["Liek malonę iš aukštai,","Savo Dvasią man įkvėpk,","Kad turtingas būčiau aš","Ir sustiprintas Tavim."]]},{number:72,title:"MALDA","sub-title":"LAUKIME PRIEŠ DIEVĄ",languages:{russian:536,english:793},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/793","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0793_i.mp3",verses:[["Siela, nurimk ir Viešpaties išlauk!","Duok Jam kalbėt, o tu tik atsakyk.","Lai Jis pradės maldoj kalbėti tau,","O tu tik kaip kanalas būki Jam."],["Siela, nurimk ir Viešpaties išlauk!","Mintis ir valią savo pažabok,","Kad patepimą jaustum Viešpaties;","Ir tikslą Jo sava malda pasiekt."],["Siela, nurimk ir Viešpaties išlauk!","Ko noris tau, ko lauki — nutylėk.","Nors Tave gundo žemiški geismai —","Svajonė Dievo būt su tavimi."],["Siela, nurimk ir Viešpaties išlauk!","Širdį ir protą nukreipki dvasios link.","Tik dvasioje bus viskas aišku tau,","Ir tik joje galėsi Jam tarnaut."],["Siela, nurimk ir Viešpaties išlauk!","Užleiski vietą — te Dvasia tekės.","Visa esybe seki paskui Jį","Sava malda tu Dievą perteiki."],["Siela, nurimk ir Viešpaties išlauk!","Kad būtum viena dvasioje su Juo.","Te Dievas laimi tave visiškai,","Kad atvaizdu Sūnaus tu taptumei."],["Siela, nurimk ir Viešpaties išlauk!","Duok Viešpačiui tekėt tavim laisvai.","Mintys ir žodžiai, ir darbai tavi","Te nuolatos Jo valioje išliks."]]},{number:73,title:"ŽODŽIO NAGRINĖJIMAS","sub-title":"MAITINIMASIS ŽODŽIU",languages:{russian:545,english:811},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/811","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0811_full.mp3",verses:[["Alksta siela ir taip trokšta dvasia,","Prie Tavo atsargų veda mane.","Tai ko man reikia — Tu vienas esi,","Troškulio, alkio neliks tik Tavy."],["Tu mano maistas, gyvybės vanduo,","Manąją dvasią tik Tu gaivini.","Valgyt ir gerti Tave nuolatos,","Melstis, skaityti — išlikti Tavy."],["Tu Dievo Žodis — pilnatvę teiki.","Tu ir Dvasia — todėl Dievas many.","Žodis Tavasis maitina mane,","Dvasioje gerti mes galim Tave."],["Maistas nužengęs Tu mums iš dangaus.","Persmeigtas Tu, kad mums gerti Tave.","Tu nesibaigiantis maistas šiandien,","Tu kaip vanduo man — gyvybės versmė."],["Žodyje Tavo — gyvybė, Dvasia,","Kad Žodžiu Savo maitintum mane.","Savo Dvasia gyveni manyje,","Kad aš galėčiau vis gerti Tave."],["Mėgaujuosi aš Tavuoju Žodžiu,","Valgysiu tol, kol man alkis išnyks.","Savo dvasia tik Tavęs aš siekiu,","Gersiu aš tol, kol manęs netroškins."],["Viešpatie, valgyt ir gert iš Tavęs,","Skaitymu valgyt, maldoj gert Tave.","Skaitant mes valgom, maldoj — geriame.","Viešpatie, Tu esi mūsų puota."],["Viešpatie, štai mes puotaujam drauge.","Dvasios pripildytas, sotus Žodžiu.","Būk, Viešpatie, Tu man puota tokia.","Ko neragavo nei vienas žmogus."]],chorus:["Duok, Dieve, valgyt, duok man ir gert;","Alkti ir trokšti neleiski man.","Duok man gyventi Tavąja jėga,","Alkti ir trokšti nenorime — ne!"]},{number:74,title:"ŽODŽIO NAGRINĖJIMAS","sub-title":"MAITINIMASIS ŽODŽIU",languages:{russian:546,english:812},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/812","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0812_full.mp3",verses:[["Taip trokštu aš Tavęs,","O Viešpatie, brangus","Ir valgyti, ir gert Tave","Maitintis Tavimi"],["Regėti veidą Jo,","Sieloj šaukiu dėl to.","Ir nuolat gerti iš Tavęs —","Taip troškulį malšint."],["Šlovingą veidą čia","Man malonu matyt.","Būti čia, nepalikt Tavęs,","Tave nuolatos matyt."],["Bendravime šiame,","Malonė Tu mana.","Palaimoj siela ir dvasia,","Džiaugiuos ramybe aš."],["Aš pasiliksiu čia","Daugiau įgyt Tavęs.","Būti maldoj ir Žodyje,","Kad lietumeis per mane."]]},{number:75,title:"BAŽNYČIA","sub-title":"",languages:{russian:570,english:841},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/841","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0841_full.mp3",verses:[["Tu mana gyvybė, gyveni many","Ir Dievo pilnatvę Savimi neši.","Prigimtim šventa Tu šventini mane,","Pergalę man duodi prisikėlime."],["Ši gyvybė Tavo apšviečia mane,","Jėzau, Tavo kraujas te nuplaus mane.","Kad gyvybės srautas liktų tėkmėje,","Kad bendraut galėčiau savo dvasioje."],["Visur prasiskverbsi, persunksi mane","Ir pakeisi mano Tu esybę.","Sutapatinsi Tu su Savim mane,","Vesi mus į brandą gyvybe sava."],["Jo gyvybė liejas gausiai į mane,","Suteiki gaivumą, iš Tavęs jėga.","Mirtis nugalėta, silpnas — jau stiprus,","Grandines nutraukei, giesmę davei mums."],["Aš Tau atiduosiu visiškai save,","Kad Savų troškimų siektum per mane.","Daugiau nesistengsiu pats pakeist save.","Netrukdysiu Tavo darbui manyje."],["Pastangų beprasmių aš nenoriu — ne!","Lai Tava gyvybė pakeičia mane.","Išugdyk mus, Dieve, kad įgytum Tu","Atvaizdą šlovingą — reiškiantį Tave."]]},{number:76,title:"BAŽNYČIA","sub-title":"JOS PATRAUKLUMAS",languages:{russian:580,english:852},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/852","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0852_full.mp3",verses:[["Tavo buveinę myliu aš —","Bažnyčią, Viešpatie.","Tavo širdis atrado čia","Džiaugsmą, ramybę."],["Save atidavei už Ją,","Kad Ją įgytumei.","Jai atiduodu kūną aš —","Mylinčiai širdžiai."],["Gyvybe Tu dėl Jos tapai,","Kad aš gyvenčiau ja.","Savęs atsižadu, kad Ji","Būtų pilna Tavęs."],["Bažnyčia Tavo Nuotaka,","Kūne regiu Tave.","Džiaugsmu, ramybe ir tiesa","Pripildo Ji mane."],["Visus turtus, kurie Joje,","Tu dovanoji man.","Aš tik Joje Tau priklausau,","Kad džiuginčiau Tave."],["Tavo buveinę myliu aš —","Bažnyčią Viešpaties.","Gyvensiu amžinai Joje","Su Tavim, Viešpatie."]]},{number:77,title:"BAŽNYČIA","sub-title":"KAIP KRISTAUS KŪNAS",languages:{russian:597,english:1126},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1226","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1226_i.mp3",verses:[["Kristaus Bažnyčia šlovinga, mes dalis Jos esame.","Džiaugiamės, kad mus surinko Viešpats čia.","Vienas Kūnas šioj visatoj ir tik Jam priklausome.","Aleliuja! Mes dėl Jo čia esame!","","Aleliuja! Kristaus Kūnas!","Mes visi nariai šio Kūno!","Pasišventėme dėl Kūno!","Aleliuja, mes visi kaip vienas čia!"],["Niekas čia neypatingas, mes visi vienybėje,","Tik vienybėje išreiškiam Dievą mes.","Ne tik vietinėj Bažnyčioj, bet ir Kūne mes vienam.","Aleliuja! Kristaus Kūne esam mes!","","Aleliuja! Kristaus Kūnas!","Tai Šėtonas bijo Kūno!","Nugalėtojai mes Kūne!","Aleliuja! Kristaus Kūne šiandien mes."],["Dievo prigimtis žibintuose auksiniuos septyniuos,","Ji nepaprasta ir šviečia mums visiems.","Tiktai Kūne turim dieviškąją prigimtį visi.","Aleliuja! Galim šviest ir mes šiandien!","","Aleliuja! Kristaus Kūnas!","Mes žibintai šiame Kūne!","Auksu spindi Kristaus Kūnas!","Aleliuja! Galim šviesti mes visi."],["Kaip išlikti mums vienybėj, būti dieviškiems ir šviest?","Aleliuja! Reikia Kristų valgyt mums!","Jis gyvybės medis, mana, puota mūsų amžina.","Aleliuja! Juo maitinkimės kasdien!","","Tiktai taip vieningi būsim!","Tik taip dieviški mes būsim!","Tada šviesti mes galėsim!","Aleliuja! Kristus maistas mūs yra."]]},{number:78,title:"BAŽNYČIA","sub-title":"KRISTAUS NUOTAKA",languages:{russian:599,english:1229},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1229","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0824_i.mp3",verses:[["Tava širdis Bažnyčioj.","Tu trokšti, Kristau, Jos.","Ar veikime, ar žody —","Tu nepamiršti Jos.","Prieš sukuriant pasaulį","Jau apie Ją mąstei;","Ir niekas negalėjo","Minčių nuo Jos nukreipt."],["Visatos dar nebuvo,","O Tu žinojai jau.","Buvai apmąstęs viską:","Kaip Nuotaką įgaut.","Nors kūriniją Savo","Širdies gelmėj matei,","Bet troškai Tu tik vieno —","Tik Nuotakos šlovėj."],["Tava valią, o Kristau —","Stipresnės nėr už ją:","Tik Nuotaką įgyti","Kad būt Tau poroje.","Nors nuodėmė mus gundo,","Išjuokus silpnume —","Tu Nuotakos Savosios","Nebeatstumsi, ne."],["Nesibaigiančia meile,","Su gailesčiu širdy","Ieškojai nepaliaujant,","Jos laukei ilgesy.","Gyvybę Tu kaip auką;","Atidavei už Ją.","Gyvybę dovanojai:","Taip gimė Nuotaka."],["Savam prisikėlime","Jai artimu tapai.","Kaip būsi sužavėtas","Kai Ji jau bus šlovėj.","Vienintelis Jos tikslas,","Tik vieno trokšta Ji:","Kad amžiais būti pora,","Kurios taip trokšta Jis."]]},{number:79,title:"BAŽNYČIA","sub-title":"KAIP VIENAS NAUJAS ŽMOGUS",languages:{russian:602,english:1232},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1232","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1232_i.mp3",verses:[["Buvom mirę nuodėmėse mes,","Bet Dievas malonės apstus.","Atgaivino ir prikėlė mus,","Pasodino mus su Kristumi danguj."],["Su šventaisiais galim mes patirt","Plotį, gylį, aukštį Dieve.","Ir suvokti Kristaus meilę mums,","Kad galėtume būt Jo pilnatvėje."],["Šiandien paslaptis mums atskleista —","Dievo planą žinome mes.","Kristų ir Bažnyčią regime,","Senas priešas tik per Juos jau gėdoje."],["O, sustiprink, Tėve, mus Dvasia,","Žmoguje vidiniame.","Meile mus įtvirtink, įšaknyk,","Per tikėjimą visų mūs širdyse."],["Gyvybingi ryšiai jungia mus,","Kristaus Kūnas esame mes.","Džiaugiamės kiekvienu mes nariu,","Kristaus Kūnas ugdomas tik meilėje."],["Kad įvykdyt Jo sumanymą,","Viešpats suvienijo mus.","Mes Jame dabar Naujas Žmogus —","Kūne Jam šlovė ir Kristuje. Amen!"]],chorus:["Jėzus kartu visus mus renka.","Vieningi sieloj mes visad.","Jo meilė išaugina mus,","Kad pasiektume pilnatvę Kristaus mes."]},{number:80,title:"BAŽNYČIA","sub-title":"PASIŠVENTIMAS JAI",languages:{russian:612,english:1248},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1248","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1248_i.mp3",verses:[["Prisiekė Dovydas:","„Statysiu aš namus Jam“.","Pašvęsiu Jam save","Ir tol aš neužmigsiu,","Kol nesurasiu Viešpačiui","Gyvenimui namų,","Kur nori Jis gyventi ten","Su Savo sūnumis."],["Kai rūpesčių srautu","Apakinti mes buvom,","Jo trokštamus namus","Palikom apleistus mes.","O, širdis mūsų Tu atgręžk,","Į kalną eisim mes,","Nelauksime kitos dienos —","Atėjome statyt."],["Atremt piktoms dienoms","Įkvėpk statyti mus Tu.","Kas išrinktas Tavęs","Kaip Dovydas te būna.","Malonėj Tavo stato jis,","Palaimintas Tavęs,","Kad įgyvendintų šiandien","Troškimą Viešpaties."],["Į tuos, ką išrinkai,","Įdėk troškimą Savo","Tau viską atiduot,","Ugdyt Bažnyčią Tavo.","Jos nugalėti negalės","Net vartai pragaro,","Nors mūs mažai, bet Tu arti,","O Viešpatie, statyk."]]},{number:81,title:"BAŽNYČIA","sub-title":"JOS BENDRAS APIBŪDINIMAS",languages:{russian:560,english:824},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/824","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0824_full.mp3",verses:[["Bažnyčia — Kristaus Kūnas,","Buveinė Tėvo Ji.","Joj Tėvas susiliejo","Su pašauktais žmonėm.","Prieš sukurdamas pasaulį","Išrinko Jis juos Sau,","Sava mirtim atpirko","Nužengęs iš dangaus."],["Mūs Viešpats Ją atgimdė,","Kai prisikėlė Jis.","Ji — Kūrinys Naujasis,","Žmogus Naujasis Ji.","Pašventinta per Žodį,","Pakrikštyta Dvasia.","Joj Kristus viešpatauja,","Su Juo pašlovinta."],["Jos pamatas tik Kristus,","Ji dieviška kaip Jis.","Tik dvasioj Kristaus mirtį","Mes galime patirt.","Tik per prisikėlimą","Statydina mus Jis,","Mes esame Jo auksas —","Gyvieji akmenys."],["Joje tik vienas Dievas —","Ir Viešpats, ir Dvasia.","Per krikštą tapo viena","Jo Kūnas Sūnuje.","Visi nariai vieningi","Tikėjime vienam,","Jie turi vieną viltį,","Jie pašaukti vienam."],["Joje nariai mes esam","Iš daugelio tautų.","Nežiūrim mes į luomus —","Visus sujungei Tu.","Nėra čia nei laisvųjų,","Nėra čia nei vergų;","Čia matomas tik Kristus —","Naujasis Jis Žmogus."],["Visatoj vienas Kūnas,","Yra Jis Viešpaties.","Per vietines bažnyčias","Mes galim Jį regėt.","Bažnyčias valdo Kristus,","Mes atsakingi Jam.","Bendravimas visatoj","Per Jį vienybėje."],["Jeruzale Naująja","Iškils prieš Kristų Ji,","Tada apsčiai Jaunikį","Išreikš pilnatvėje.","Šviestuvas — Viešpats Kristus,","O Dievas — jo šviesa.","Ir kiekvienam žibinte","Vienoda išraiška."]]},{number:82,title:"BAŽNYČIA","sub-title":"VIEŠPATIES KAIMENĖ",languages:{russian:593,english:1221},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1221","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0260_i.mp3",verses:[["Išvedė Jėzus iš gardo,","Tapo Ganytoju mums.","Žaliose lankose gano,","Dosniai mums teikia turtus."],["Mus susiskaldžiusius rado,","Alkanus rado Jis mus.","Į gerą žemę atvedęs,","Atvėrė dvasią Jis mums."],["Viešpats — Jis mūsų ganykla.","Viešpats — mūs maistas yra.","Susirinkimuose mūsų","Mus pamaitina visad."],["Su Juo gyvenam ant kalno,","Mus atgaivina rasa.","Troškulio jau nebeliko —","Viešpats vanduo mūs yra."],["Jis mūsų džiaugsmas, ramybė —","Nėra pavojaus čia mums.","Savo kaimenę Jis saugo","Ištikimu buvimu."]],chorus:["Šlovingoj bažnyčioj","Mes čia puotaujam su Juo.","Tie, kurie visad vienybėj,","Mėgausis gyvybe Jo."]},{number:83,title:"BAŽNYČIA","sub-title":"KAIP MŪSŲ NAMAI IR RAMYBĖ",languages:{russian:607,english:1237},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1237","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1237_i.mp3",verses:[["Sodas dieviškas Bažnyčioje, mes pasiliksim čia,","Kur augins Sava gyvybe Kristus mus.","Ir visiems Šventiesiems pilnas pasimėgavimo Jis,","Toks brangus, prieinamas, kad valgyt Jį."],["Ne gamykla, ne mokykla, ne koplyčia danguje —","Sodas čia kur Viešpats gali sėt ir pjaut.","Jis visus mus čia surinko — sodas Jo mes esame,","Dirbdamas mumyse nori mus augint."],["Čia gyvybės medis auga, duoda vaisius jisai mums,","Pilnas jis gyvybės, mums prieinamas.","Negudrauk ir nesipriešink, skink ir valgyk šiuos vaisius","Ir priimk saldumą Jėzaus visada."],["Šalia šito medžio Dievas, Kristuje mums teka Jis,","Kad sausumą ir sunkumus nugalėt.","Aleliuja! Jėzus nori į šį sodą mums tekėt","Ir drėkint mus, kad gyvybėj augtume."],["Gal sode tu nelaimingas, o gal nedėkingas tu","Už gausumą tekantį iš Dievo mums.","Dvasioje su Juo puotaujant prisipildyki džiaugsmu,","Kad galėtum gausiai vaisių nešti tu."]],chorus:["Koks laimingas aš šiame sode,","Augu Viešpaties malonėje.","Valgyt nuo gyvybės medžio","Nėra nieko maloniau,","Troškulį malšint gyvybės vandeniu."]},{number:84,title:"SUSIRINKIMAS","sub-title":"KRISTAUS IŠRAIŠKA",languages:{russian:622,english:864},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/864","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0864_full.mp3",verses:[["Mums būnant susirinkime,","Jei mes išliekam Kristuje,","Atnešim Dievui perteklių —","Išreikšim Kristų mes."],["Gyvenam, kaunamės Jame,","Jo siekiam dieną ir nakčia;","Su perteklium ateisime","Būt Kristaus išraiška."],["Ir kuo beužsiimtume,","Išliekame mes Kristuje;","Kad susitikus mums visiems,","Mes Kristų reikštume."],["Mes perteikiame Kristų čia,","Mes Juo dalijamės visi.","Saldumą Dievo patiriam,","Išreiškiam Kristų čia."],["Atnešim Dievui Kristų mes","Prikeltą ir išaukštintą,","Kad Dievui mes patiktume,","Mes Kristų reiškiame."],["Mūs Jėzus Kristus — centras mums,","Tikrovė ir tarnavimas.","Susirinkimuose šiuose","Mes turim Jį išreikšt."],["Ir liudijimas, ir malda.","Bendravimas tarpusavy;","Ir dovanos, kurios Jame —","Tai turi reikšti Jį."],["Mes Dievą — Tėvą šlovinam,","Jo Sūnų — Kristų aukštinam.","Ir siekiam susirinkime —","Būt Kristaus išraiška."]],chorus:["Išreiškim Kristų mes,","Išreiškim Kristų mes.","Atnešdami Jo perteklių,","Išreiškim Kristų mes."]},{number:85,title:"DVASINĖ KOVA","sub-title":"PAKLUSNUMAS JAM",languages:{russian:634,english:880},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/880","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0314_i.mp3",verses:[["Nepailstamai kartosim:","Tėvui — „taip“, o priešui — „ne“","Kad sumanymas šis Dievo,","Būtų realybėje.","Norim būti mes paklusnūs,","Tavo valią vykdyti.","Dvasioje tiktai gyventi,","Tavo norus pildyti."],["Nepailstamai kartosim:","Tėvui — „taip“, o priešui — „ne“","Žodžiais šiais visi kovosim","Dvasinėje kovoje.","Saugok mus ir laimink, Dieve.","Duok malonės dienai šiai.","Mes kovoti pasirengę","Ir kovosim atkakliai."],["Nepailstamai kartosim:","Tėvui — „taip“, o priešui — „ne“","Nors per sielvartus ir einam,","Mes vis tiek kovosime.","Gelbėk, išlaikyk mus, Dieve,","Siekti tikslo nuolatos.","Šauksimės mes Tavo vardo","Mes visi be atvangos."]]},{number:86,title:"DVASINĖ KOVA","sub-title":"ŽEMĖS UŽVALDYMAS",languages:{russian:648,english:1287},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1287","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1118_i.mp3",verses:[["Užimsime mes žemę Dievo duotą mums,","Nes Kristuje mes galim daug pasiekt.","Kad ją užimt, mes esame aprūpinti","Dvasia, Žodžiu, Bažnyčia ir Krauju."],["Mes turim kraują! Jo dėka ryžtingi mes.","Mūs Kristus Dievui tinkama auka.","Ir kraujas šis apvalo mus visus kasdien,","Todėl apstus mums Dievas malone."],["Mes turim Žodį — maistas mūs yra kasdien,","Skaitydami kartojame „Amen“.","Šis Žodis kalba mūsų viduje kasdien","Ir veda mus Jis Viešpaties šlovėn."],["Mes turim Dvasią! Tiesą turime per Ją.","Ji nepriklausoma nuo mūs jausmų.","Dvasia gyvena mūsų viduje kartu","Ir veda mus siauru tiesos keliu."],["Bažnyčią turim! Tik joje mes galime","Pilnatvę visą Viešpaties turėt.","Ir visaapimantis Kristus taps visų","Ir bus šventųjų viskas visame."]],chorus:["* Sesės *","Tai užvaldykim žemę, broliai!","Ją Dievas mums atidavė.","Greičiau užimkim ir užvaldykim","Kaip bus šlovinga ją įgyt.","* Broliai *","Tai užvaldykim žemę, sesės!","Tikėdami giliai širdy.","Ir neikim patys, kaip daugel metų,","Juk Viešpats veda mus pirmyn."]},{number:87,title:"KRISTUS MUMYSE","sub-title":"ŠLOVĖS VILTIS",languages:{russian:686,english:948},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/948","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0948_i.mp3",verses:[["Paslaptis didinga mums jau atskleista:","Kristus — Dievo amžina tiesa.","Įsikūnijimas Dievo Jis yra,","Amžina viltis tiktai Jame."],["Jis atgimdė dvasią mano mirusią,","Sielą atmainys pagal Save.","Kūnas mano Juo pašlovintas jau bus,","Tapsime kaip Jis pilnatvėje."],["Prigimty, gyvybėj — viena esame,","Šlovę Jo paveldėsime mes.","Pasiliksiu amžiais prieš Jo veidą aš,","Atvaizdu aš tapsiu Viešpaties."]],chorus:["Kaip šlovinga, Kristus manyje!","Jis gyvybė, lieku viltyje.","Ir kaip paslaptis Jis mano dvasioje","Tik per Jį aš būsiu šlovėje."]},{number:88,title:"ŠLOVĖS VILTIS","sub-title":"KRISTUS, KAIP MŪSŲ PAŠLOVINIMAS",languages:{russian:687,english:949},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/949","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0503_i.mp3",verses:[["Šlovės viltis tik Kristus, gyvybė Jis mana,","Suteikęs atgimimą, Jis persunkė mane.","Ateis, kad mano kūną pakeistų iš tiesų,","Kad tapčiau aš šlovingu Jo atvaizdu."],["Šlovės viltis Jis mūsų, Jis Dievo šlovėje.","Ateis, kad susiliečiau su Juo pilnatvėje","Jis atneša man Dievą, kad aš dalinčiau Jį","Kad Jo šlovėj per amžius pasilikčiau."],["Šlovės viltis Jis mūsų, Jisai atpirko mus.","Ateis ir mano kūnas tada bus nemarus.","Tik pergale Savąja Jis mirtį sunaikins.","Atėjęs mano kūną pašlovins."],["Šlovės viltis tik Kristus, istorija mana,","Aš gyvas Jo gyvybe, nes viena esame.","Ateis, kad būčiau laisvėj, kad būčiau šlovėje;","Ir viena amžinybėj mes tapsime."]],chorus:["Ateis! Ateis! Mane pašlovint ateis!","Pakeisti kūną man ateis į Savo atvaizdą.","Ateis! Ateis! Išpirkti mus visus!","Šlovės viltis Jis mūsų, pašlovint mus ateis."]},{number:89,title:"ŠLOVĖS VILTIS","sub-title":"VESTUVIU DIENA",languages:{russian:711,english:1314},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1314","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0175_i.mp3",verses:[["Greit Viešpats vėl sugrįš,","Laukiame Jo visi.","Palaimingiausia diena ši bus.","Galime tik Tavy","Mylėt ir būt gyvi,","Esi Jaunikis mūs visų."],["Nepastebėsime,","Kaip Jį išgirsime","Ir danguje mes išvysim Jį.","Kokia palaiminta","Bus ši akimirka —","Jaunikis su Jaunąja bus."],["Dienos trumpėja jau.","Greit jos prabėgs, tačiau","Jėzus, mūs Viešpats, pas mus ateis.","Trokštame su Tavim","Vienybėje gyvent —","Tu pakerėjai mūs širdis."],["Susitikimas greit,","Viešpats suskubs ateit","Nuotakos Savo pasiimt.","O, aleliuja!","Džiaugiasi mūs dvasia,","Palaimintas Jaunikis bus."]]},{number:90,title:"KARALYSTĖ","sub-title":"JOS SĖKLA",languages:{english:1299},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1299","melody-url":"https://www.hymnal.net/Hymns/NewTunes/mp3/e1299_new.mp3",verses:[["Mūs Kūrėjas turi tikslą, dėl kurio mes žemėje,","Atspindėdami Jo šlovę, valdžią Jo išreiškiame.","Nors žmogus ir nusidėjo, nebematė jau prasmės —","Mūsų Dievo Karalyste per Jo Sūnų suklestės."],["Jėzus, Gelbėtojas mūsų, Tėvo siųstas žemėje.","Jis dabar — Dvasia, Gyvybe gali augti žmoguje.","Tarsi sėkla mūsų dvasioj Kristus auga, plečiasi;","Kol mes tapsime galiausiai Jo gyvybės vaisiumi."],["Broliai, sesės, laikas trumpas — Kristus laukia Nuotakos.","Jis — sugrįžti neuždelstų, jei gyvybė sulapos.","Nesistenkime kovoti, o tik savo vidumi","Atsigręžkime į Kristų, persikeiskime į Jį."]],chorus:["Jėzaus Kristaus Karalystė, nepaisant priešiškų planų,","Reali, tvirta, galinga auga širdyse žmonių.","Tik beverčiai išvedžiojimai mus moko: laukite mirties,","Kristus stato Karalystę žemėje dabar išties."]},{number:91,title:"GEROJI ŽINIA","sub-title":"KRAUJAS",languages:{russian:744,english:1007},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1007","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1007_i.mp3",verses:[["Ar žinai tu kraujo jėgą? Pasakyk.","Ar nuplautas tu Jėzaus krauju?","Ar pasitiki malone Dievo tu?","Ar nuplautas tu Jėzaus krauju?"],["Ar kiekvieną dieną seki Vieš pačiu?","Ar nuplautas tu Jėzaus krauju?","Ir ar ieškai sielvarte ramybės tu?","Ar nuplautas tu Jėzaus krauju?"],["Kai Jaunikis grįš, ar rūbas bus švarus?","Ar nuplautas tu Jėzaus krauju?","Ar švytėsi tu sava siela prieš Jį?","Ar nuplautas tu Jėzaus krauju?"],["Nuodėmingus drabužius paliki tu.","Ar nuplautas tu Jėzaus krauju?","Sielos nešvarą nuplauki tu krauju,","Būk nuplautas tu Jėzaus krauju."]],chorus:["Ar švarus tu? Kraujas Jo","Gali nuodėmes tavo nuplaut.","Avinėlio kraujas liejosi už mus,","Kad tu būtum šiandieną skaistus."]},{number:92,title:"GEROJI ŽINIA","sub-title":"ATĖJIMAS PAS VIEŠPATĮ",languages:{russian:765,english:1048},"hymnal.net-url":"https://www.hymnal.net/en/hymn/nt/1048","melody-url":"https://www.hymnal.net/Hymns/NewTunes/mp3/e1048_new.mp3",verses:[["Nieks neišteisina manęs,","Tik Tavo kraujas šventas.","Žinau aš, kad šauki mane,","Einu aš, Jėzau, pas Tave."],["Einu dabar, nelauksiu aš,","Kol sieloje nurims audra.","Šventu krauju nuplauk mane,","Einu aš, Jėzau, pas Tave."],["Nors mane drasko viduje","Ramybės nėra širdyje.","Esu kovoj ir baimėje,","Einu aš, Jėzau pas Tave."],["Koks alkanas ir aklas aš,","Bet Tu esi turtingas man.","Aš viską rasiu Tavyje,","Einu aš, Jėzau, pas Tave."],["Koks aš esu — priimsi Tu,","Tikiu aš Tavo pažadu.","Išliesi man malonę,","Einu aš, Jėzau, pas Tave."],["Beribė meilė Tavo man","Pašalino kliūtis visas.","Dabar aš Tavo meilėje,","Einu aš, Jėzau, pas Tave."]]},{number:93,title:"GEROJI ŽINIA","sub-title":"ATĖJIMAS PAS VIEŠPATĮ",languages:{russian:767,english:1051},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1051","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1051_i.mp3",verses:[["Girdžiu šaukimą aš,","Tu pas Save kvieti.","Savu šventu krauju mane","Tu nori apvalyt."],["Nors silpnas aš esu","Ir nuodėmė many,","Nuplauni Tu mane krauju,","Šventumą suteiki."],["O Jėzau, Tu užbaik","Šį darbą manyje.","Suteik malonę man, meldžiu,","Ją lieki į mane."],["Kas ištikimas Jam —","Suteikia viską Jis.","Tik nenustok į Jį tikėt,","Laikykis Jo širdy."],["Garbė už kraują Jo,","Garbė, nes šventas Jis.","Palaimą teikia Kristus mums —","Tik Jo dėka tyri."]],chorus:["Pas Tave einu,","Mano Viešpatie.","Nuodėmes manas nuplauk,","Brangiausiuoju krauju."]},{number:94,title:"GEROJI ŽINIA","sub-title":"ATĖJIMAS PAS VIEŠPATĮ",languages:{russian:768,english:1052},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1052","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1052_i.mp3",verses:[["Toks Dievui tolimas buvau,","Bet einu namo.","Aš nuodėmėje gyvenau,","Bet einu namo."],["Prabėgo metų daug tuščiai,","Aš einu namo.","Su ašarom atgailavau,","Aš einu namo."],["Pavargęs būti nuodėmėj,","Aš einu namo.","Pasitikiu tik Tavimi,","Aš einu namo."],["Ir skausmas širdį varsto man,","Aš einu namo.","Jėgų man duoki ir vilties,","Aš einu namo."],["Visa viltis tik Tavyje,","Aš einu namo.","Pats Viešpats mirė už mane,","Aš einu namo."],["Apsivalysiu Jo krauju,","Aš einu namo.","Nuplauk už sniegą dar balčiau,","Aš einu namo."]],chorus:["Viešpatie, aš einu,","Aš einu namo!","Viešpatie, sutik mane!","Aš einu namo."]},{number:95,title:"GEROJI ŽINIA","sub-title":"JO ŠAUKIMASIS",languages:{russian:770,english:1055},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1055","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0267_i.mp3",verses:[["Nuodėmingas, be vilties aš,","Skausmuose, tamsoj, mirty.","Štai dabar, o Gelbėtojau!","Gelbėki mane meldžiu.","Gelbėki, gelbėki,","Tu mane išlaisvinki.","Nuodėmingas aš ir kritęs —","Štai dabar išgelbėki."]]},{number:96,title:"GEROJI ŽINIA","sub-title":"ŠAUKIMASIS VIEŠPATIES",languages:{russian:773,english:1059},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1059","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1059_i.mp3",verses:[["Noriu būt prie kryžiaus aš","Ir siela regėti.","Kaip malonė Jo žmonėms","Nenustoja lietis."],["Atradau prie kryžiaus aš","Jėgą ir malonę.","Jėzau, lyg žvaigždė Tu man","Apšvietei kelionę."],["Pamačiau prie kryžiaus aš","Dievo Avinėlį.","Man brangiausiu tapo Jis.","Juo susižavėjau."],["Su tikėjimu, viltim.","Čia prie kryžiaus lauksiu.","Kai mus Dievas lieps pašaukt,","Į Jo didį džiaugsmą."]],chorus:["Kryžius Jo, kryžius Jo","Veda mus į šlovę.","Jo išgelbėjimas čia.","Liejas čia malonė."]},{number:97,title:"GYVYBĖ","sub-title":"GEROJI ŽINIA MUMS",languages:{russian:750,english:1017},"hymnal.net-url":"","melody-url":"",verses:[["Dievas Kristuje atėjo,","Tapo Žmogumi kaip mes.","Nukryžiuotas buvo Jis,","Kad būtume mes laisvėje.","Prisikėlė ir atėjo","Kaip Dvasia Jisai pas mus,","Kad būt gyvybe mums."]],chorus:["Jam šlovė! O aleliuja!","Jam šlovė! O aleliuja!","Jam šlovė! O aleliuja!","Gyvybė Kristus mums!","","Pergalė! O aleliuja!","Pergalė! O aleliuja!","Pergalė! O aleliuja!","Jis tapo viskuo mums!"]},{number:98,title:"GEROJI ŽINIA",languages:{russian:789,english:1331},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1331","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1331_i.mp3",verses:[["Tu paragauk, koks geras Jis,","Ir regėsi tu, koks geras Jis.","Jis geras tau ir Jis geras man,","Ką beveiktum tu — visad ragauk."],["Viešpatie Jėzau — šauki Jį!","Viešpatie Jėzau — šauki Jį!","Malonė po malonės,","Tau ji gyvenimą staiga pakeis."],["Širdis pajus, kai meilė plūs,","Širdis pajus, kai meilė plūs.","Ir iš dangaus pripildys ją,","Pažinsi Dievą tu sava dvasia."],["Už Jo gyvybę šlovink Jį,","Ji mumyse — tu šlovink Jį.","Išliejo Kristų į tave,","Jisai gyvybė — gyvenki ja."]]},{number:99,title:"RAŠTO VIETOS GIEDOJIMUI","sub-title":"PSALMĖ 116:12—13",languages:{russian:793,english:1336},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1336","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1336_i.mp3",verses:[["Kuo Viešpačiui atlyginsiu","Už visa, už visa, už visa ką padarė Jis?","Priimsiu gelbėjimo taurę,","Ir šauksiuos, ir šauksiuos, ir šauksiuos","Vardo mano Viešpaties."]]},{number:100,title:"RAŠTO VIETOS GIEDOJIMUI","sub-title":"EVANGELIJA PAGAL JONĄ 13:34",languages:{russian:799,english:1343},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1343","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1154_i.mp3",verses:[["Naują įsakymą Aš duodu jums:","„Tad mylėkit viens kitą, kaip Aš myliu jus“","„Tad mylėkit viens kitą, kaip Aš myliu jus“","Pažins visi jus, kad mokiniai jūs Mano,","Jei meilę saugosit visad tarp savęs.","Pažins visi jus, kad mokiniai jūs Mano,","Jei meilę saugosit visad tarp savęs."]]},{number:101,title:"GYRIUS VIEŠPAČIUI","sub-title":"JO VARDAS",languages:{russian:62,english:1084},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1084","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1084_i.mp3",verses:[["Jo vardas man — patepimas:","Jėzus, Jėzus, Jėzus, Jėzus!","Jo vardas man — patepimas,","Jo vardas man — patepimas."],["Mergelės už tai myli Jį:","Jėzus, Jėzus, Jėzus, Jėzus!","Mergelės už tai myli Jį,","Mergelės už tai myli Jį."],["Jo meilė, kaip vynas saldus:","Jėzus, Jėzus, Jėzus, Jėzus!","Jo meilė, kaip vynas saldus,","Jo meilė, kaip vynas saldus."],["Žavėki, mes seksim Tavim:","Jėzau, Jėzau, Jėzau, Jėzau!","Žavėki, mes seksim Tavim,","Žavėki, mes seksim Tavim."],["Pamiltas! Tu man nuostabus:","Jėzau, Jėzau, Jėzau, Jėzau!","Pamiltas! Tu man nuostabus,","Pamiltas! Tu man nuostabus."],["Siela rado Mylimąjį:","Jėzus, Jėzus, Jėzus, Jėzus!","Siela rado Mylimąjį,","Siela rado Mylimąjį."],["Visa siela myliu Tave:","Jėzau, Jėzau, Jėzau, Jėzau!","Visa siela myliu Tave,","Visa siela myliu Tave."],["Laikysiuos aš Mylimojo:","Jėzaus, Jėzaus, Jėzaus, Jėzaus!","Laikysiuos aš Mylimojo,","Laikysiuos aš Mylimojo."],["Ateik, o Pamiltas, visų:","Jėzau, Jėzau, Jėzau, Jėzau!","Ateik, o Pamiltas, visų,","Ateik, o Pamiltas, visų."],["Visa širdim mylim Tave:","Jėzau, Jėzau, Jėzau, Jėzau!","Visa širdim mylim Tave,","Visa širdim mylim Tave."]]},{number:102,title:"KRISTAUS, KAIP GANYTOJO, PATYRIMAS",languages:{russian:390,english:1170},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/1170","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e1170_i.mp3",verses:[["Viešpats Ganytojas amžiams,","Jis poilsio mano vieta.","Prie vandenų Jis mane veda","Ir taip Jis pripildo mane."],["Ganytojas ir ganykla Jis","Ramybės vanduo Jis yra.","Aš dvasioj iš Jo turtų valgau,","Geriu Jo malonę visad."],["Tik Jis — ištaisys mano sielą","Ir valią ir protą, jausmus.","Nors tenka per slėnį man eiti —","Lydės Jis mane — Nuostabus."],["Dėl manęs Jis paruošė puotą,","Kad nugalėt priešą per ją.","Aliejum man patepė galvą,","Ir taurė džiaugsmu sklidina."],["Dabar Jo namuos gyvenu aš,","Pripildytas Dievo džiaugsmu.","Šiam džiaugsmui nėra jokių žodžių,","Nes džiaugsmas šis — Viešpats brangus."]],chorus:["Jo palaima ir malonė bus su manim","Per visas, visas mano dienas.","Jo palaima ir malonė bus su manim","Per visas, visas mano dienas.","Pasiliksiu aš amžiams Jo namuose","Ir puotausiu prie stalo, kurs padengtas man.","Jo palaima ir malonė bus su manim","Per visas, visas mano dienas."]},{number:103,title:"RAŠTO VIETOS GIEDOJIMUI","sub-title":"DEBOROS GIESMĖ",verses:[["Pabusk, pabusk, Debora!","Pabusk, pabusk, giesmę giedok.","Pabusk, Barakai, pakilki drąsiai,","Tu laimėjai!"]],chorus:["Tekrinta Tavo priešai, o Jehova!","Bet tiems, kas myli Tave,","Suteiki džiaugsmą!","Pabuskite, Dievo vaikai.","Pakilkite kaip karžygiai,","Kovokit kaip Jehelė","Su Dievo priešu!","","Tekrinta Tavo priešai, o Jehova!","Bet tiems, kas myli Tave,","Suteiki džiaugsmą!"]},{number:104,title:"RAŠTO VIETOS GIEDOJIMUI","sub-title":"RŪTOS GIESMĖ",verses:[["Kur eisi tu, eisiu aš.","Kur busi tu, busiu aš!","Tavoji tauta — bus mano tauta.","Tavo Dievas — mano Dievu!"]]},{number:105,title:"GALUTINIS APSIREIŠKIMAS","sub-title":"DIEVO AMŽINASIS TIKSLAS","hymnal.net-url":"https://www.hymnal.net/en/hymn/ns/28","melody-url":"https://www.hymnal.net/Hymns/NewSongs/mp3/ns0028.mp3",verses:[["Pašaukė visus mus Dievas,","Dėl Dievo namų statybos.","Buvo tam Jisai paruoštas","Ir dabar užbaigtas Jis.","Kaip Dvasia Jis mumyse,","Dievo duota mums dalia,","Kad pilnai išgelbėti mes","Taptume tokie kaip Jis."],["Pavyzdys mums Jėzus buvo,","Kaip Dievas — žmogus gyveno.","Sielos negailėdamas Jis","Klusniai ėjo link mirties.","Tik vienintelis Jis buvo,","Atkartojimu Jo tapom.","Padaugėjimas Jo esam,","Susilieję kaip grūdai mes."],["Numarindami mes sielą","Kristaus prisikėlime,","Sulieta dvasia gyvenam,","Mes atmesdami save.","Ir dabar gyvenam Kūne,","Nugalim kiekvieną dieną,","Verždamiesi į Sioną","Atėjimo Jėzaus laukiam."],["Dvasioje sulietoj Dievas","Ir žmogus kartu gyvena,","Kaip vieningas organizmas","Esam dviese visuma.","Šitas matymas tevaldo","Mus gyvenime kasdieniam,","Taip įgys Sau Viešpats Kūną,","Spindintį kaip Šventas miestas."]],chorus:["Te matymas Jo valdo mus visus,","Namų statybos mūsų Viešpaties.","Ir degina, kol nepradės liepsnoti mano dvasia,","Gyva stipria dvasia mes trokštame","Užbaigt Jo planą, Naują Jeruzalę, Aleliuja!"]},{number:106,title:"ŠLOVĖ VIEŠPAČIUI","sub-title":"JO PADIDĖJIMAS",languages:{russian:152,english:203},"hymnal.net-url":"https://www.hymnal.net/en/hymn/h/203","melody-url":"https://www.hymnal.net/Hymns/Hymnal/mp3/e0203_full.mp3",verses:[["Amžinybėj Tėvo glėby,","Prieš visas pradžių pradžias.","Viengimiu Sūnum buvai Tu","Savo Tėvo šlovėje.","Kai atidavė Tave Jis,","Tu tas pats asmuo buvai,","Dvasioje pilnatvę Tavo","Mums tada Tu paskelbei."],["Tu mirei ir prisikėlei,","Pirmgimiu Sūnum tapai.","Atkartojimas mes Tavo,","Jo gyvybę suteikei.","Tavyje atgimę esam —","Dievo daugelis sūnų,","Iš tiesų mes Tavo broliai,","Tokie patys, kaip ir Tu."],["Viešpatie, kaip kviečio grūdas","Žemėn Tu kritai numirt,","O numiręs prisikėlei,","Gyvybe išplėtei.","Tavo prigimty atgimę","Mes kaip daugelis grūdų,","Susilieję vienoj duonoj,","Pilnumoj išreikštas Tu."],["Atkūrimas, Tavo Kūnas,","Išraiška pilnatvėje,","Esam Tavo pratęsimas","Ir Tavoji nuotaka.","Padaugėjimas gyvybės,","Visas ūgis, perteklius,","Mes — išsiplėtimas Tavo,","Mūsų Tu esi Galva."]]}];export{i as j};
